('article_article_category', '?template("article_head")?\n<ul class="tab left">\n<li>基本信息</a></li>\n<li>分类标签</li>\n<:if test="?article.jtype is ''3''?">\n<li><a href="?site(''article/article/photo'')?/?articleId?">相册管理</a></li>\n</:if>\n<:if test="?article.jtype is ''6''?">\n<li><a href="?site(''article/article/special'')?/?articleId?">专题管理</a></li>\n</:if>\n<li><a href="?site(''article/home/detail'')?/?artice.articleId?" target="_blank">预览</a></li>\n</ul>\n<form action="?site(''article/article/category'')?" method="post" id="act-jump"><input type="hidden"  name="articleId" value="?articleId?" />\n<table>\n<tr style="background:#F6F6F6;">\n<td style="text-align:left">\n<div style="width:325px;float:left;height:540px;overflow:auto">\n<strong>分类</strong>\n<ul id="cate8912" style="overflow:auto;width:300px;margin-left:20px;">\n?api(''core'').categoryOption("0",0)?\n</ul>\n</div>\n<div style="width:325px;margin-left:45px;float:left;height:540px;overflow:auto">\n</ul>\n</div>\n</td>\n</tr>\n<tr style="background:#F6F6F6"><td><input type="submit" class="bt" value="提交" /> <input type="reset" class="bt" value="重置" /></td></tr>\n</table>\n</form>\n</div>\n<script type="text/javascript">\n    function setTag(obj) {\n        id = $(obj).val();\n        var tags = ''no'';\n        if ($(obj).attr(''checked'')) {\n            $(''.cls'' + id).show();\n            txt = $(obj).next().text();\n            if ($(''div'').data(id)) {\n                tags = $(''div'').data(id);\n            } else {\n                $.ajax({\n                    url: ''?site("core/category/tag_option")?/'' + id,\n                    type: ''POST'',\n                    async: false,\n                    success: function (msg) {\n                        if (msg == '''')\n                            tags = ''no'';\n                        else\n                            tags = msg;\n                        $("div").data(id, tags);\n                    },\n                    error: function (obj) {