('base_skin_config', '?template("base_head")?\r\n<div id="postion">\r\n<a href="?site(''jabinfo012738'')?">桌面</a><a href="?site(''base/skin/home'')?">皮肤中心</a>\r\n皮肤配置</div>\r\n<div class="actions"><ul>\r\n<li><a href="#" onclick="mywin(''?site(''base/skin/config_add'')?/?skin_id?'',''?site(''base/skin/config_add'')?'',''添加配置'')">添加配置</a></li>\r\n<li><a href="#" onclick="mywin(''?site(''base/skin/config_import'')?/?skin_id?'',''?site(''base/skin/config_import'')?'',''导入配置'')">导入配置</a></li>\r\n<li><a href="#" onclick="del(''?site(''base/skin/config_restore'')?/?skin_id?'')">使用默认配置</a></li>\r\n</ul></div>\r\n<form action="?site(''base/skin/config'')?" enctype="multipart/form-data" method="post">\r\n<table class="ctable" cellspacing="0" cellpadding="0">\r\n<:foreach collection="?configList?" var="data">\r\n<tr><td style="text-align:right;padding-right:8px">?data.Title?-[?data.Key?]</td><td style="text-align:left">?init(data)?</td><td>\r\n<a href="#" onclick="mywin(''?site(''base/skin/config_edit'')?/?data.Sk_config_id?'',''?site(''base/skin/config_edit'')?'',''编辑?data.Title?'')">编辑</a>\r\n<a href="#" onclick="del(''?site(''base/skin/config_del'')?/?data.Sk_config_id?'',this)">删除</a>\r\n</td></tr>\r\n</:foreach>\r\n<tr><td width="15%"></td><td style="text-align:left" width="70%"><input type="submit" class="bt" value="提交" /> <input type="reset" class="bt" value="重置" />\r\n<input type="hidden" name="skin_id" value="?skin_id?" />\r\n</td><td width="15%"></td></tr>\r\n</table>\r\n</form>\r\n?template("base_foot")?', '1002032132328300875', 1323452670, 1338487680, '1204251659419211169', 5, ''),
('base_skin_config_add', '<tr><th>名称</th><td><input type="text" name="title" maxlength="15" id="Text1" /></td></tr>\r\n<tr><th>配置键</th><td><input type="text" name="key" maxlength="10" id="key-char" />\r\n<tr><th>类型</th><td>\r\n<select name="jtype" onchange="if(this.value==''3''){$(''.ck_data'').show()}else{$(''.ck_data'').hide()}">\r\n<option value="0">文字</option>\r\n<option value="3">图片</option>\r\n<option value="4">Flash</option>\r\n</select>\r\n</td></tr>\r\n<tr><th>默认值</th><td><input type="text" name="devalue" /></td></tr>\r\n<tr class="ck_data" style="display:none"><th>图片宽</th><td><input type="text" value="0" id="width-digit" name="width" /></td></tr>\r\n<tr class="ck_data" style="display:none"><th>图片高</th><td><input type="text" value="0" id="height-digit" name="height" /></td></tr>\r\n<tr><th>排序</th><td><input type="text" id="Text2" value="10" name="jindex" /><input type="hidden" name="skin_id" value="?skin_id?" id="act-jump" /></td></tr>', '1002032132328300875', 1323454590, 1338653310, '1204251659419211169', 5, ''),
('base_skin_config_edit', '<tr><th>名称</th><td><input type="text" name="title" maxlength="15" value="?config.Title?" id="title-need" /></td></tr>\n<tr><th>默认值</th><td><input type="text" name="devalue" maxlength="200" value="?config.Devalue?" /></td></tr>\n<tr><th>排序</th><td><input type="text" id="jindex-digit" value="10" style="width:120px" value="?config.Jindex?" name="jindex" />&nbsp;\n<input type="checkbox" style="width:15px" name="default" value="1" />使用默认值\n<input type="hidden" name="sk_config_id" value="?sk_config_id?" id="act-jump" />\n</td></tr>', '1002032132328300875', 1323463680, 1323465000, '1002032132328300875', 5, ''),
('base_skin_config_import', '<tr><th>目标皮肤</th><td>\n<select name="object_id">\n<:foreach collection="?skins?" var="skin">\n<option value="?skin.Skin_id?">?skin.Title?</option>\n</:foreach>\n</select>\n<input type="hidden" name="skin_id" value="?skin_id?" id="act-jump" />\n</td></tr>', '1002032132328300875', 1323465730, 1323465730, '1002032132328300875', 5, ''),
('base_skin_set', '<tr><th>名称</th><td><input type="text" name="title" maxlength="50" value="?skin.Title?" />\r\n<input type="hidden" name="skin_id" value="?skin.Skin_id?" /></td></tr>\r\n<tr><th>类型</th><td>\r\n<select name="category" id="category-?skin.Category?">\r\n    <option value="">未发布</option>\r\n    <:foreach collection="?category.Keys?" var="key"><option value="?key?">?category[key]?</option></:foreach>\r\n</select>\r\n</td></tr>\r\n<tr><th>颜色</th><td><select name="color">\r\n    <:foreach collection="?color.Keys?" var="key"><option value="?key?">?color[key]?</option></:foreach>\r\n    </select></td></tr>\r\n<tr><th></th><td></td></tr>\r\n<tr><th>单价</th><td><input type="text" name="price" value="?skin.Price?" /></td></tr>\r\n<tr><th>排序</th><td><input type="text" name="jindex" value="?skin.Jindex?" /></td></tr>\r\n<tr><th>作者</th><td><input type="text" name="author" maxlength="10" value="?skin.Author?" /></td></tr>\r\n<tr><th>预览图</th><td><input type="file" name="pre" /></td></tr>\r\n<tr><th>描述</th><td><textarea name="contact" rows="8" cols="26">?skin.Contact?</textarea></td></tr>', '1002032132328300875', 1318430210, 1339092350, '1002032132328300875', 4, ''),
('base_skin_site', '?header("base_head")?\n<div id="postion">\n<a href="?site(''jabinfo012738'')?">桌面</a>参数设置</div>\n<div class="actions"><ul>\n<li>参数设置</li>\n<li><a href="#" onclick="del(''?site(''base/skin/config_restore'')?'')">恢复默认配置</a></li>\n</ul></div>\n<form action="?site(''base/skin/config'')?" enctype="multipart/form-data" method="post">\n<table class="ctable" cellspacing="0" cellpadding="0">\n<:foreach collection="?configList?" var="data">\n<tr><td style="text-align:right;padding-right:8px">?data.Title?-[?data.Key?]</td><td style="text-align:left">?init(data)?</td></tr>\n</:foreach>\n<tr><td width="15%"></td><td style="text-align:left" width="85%"><input type="submit" class="bt" value="提交" /> <input type="reset" class="bt" value="重置" />\n<input type="hidden" name="skin_id" value="?skin_id?" /></td></tr>\n</table>\n</form>\n?template("base_foot")?', '1002032132328300875', 1324143870, 1324146180, '1002032132328300875', 5, ''),
('base_skin_user', '?header("base_head")?\n<style type="text/css">\n#skin li{float:left;width:192;height:218px;margin:8px;padding:4px;border:1px solid #CCC;overflow:hidden;display:block;text-align:center}\n#skin img{border:0}\n#skin span{margin-right:10px;}\n#skin .cur{border:green 1px solid !important;}\n</style>\n<div id="postion"><a href="?site(''jabinfo012738'')?">桌面</a>皮肤中心</div>\n<div class="actions"><ul><li>皮肤中心</li><li><a href="#" onclick="mywin(''?site(''base/user/cache'')?'',''?site(''base/user/cache'')?'',''更新缓存'')">更新缓存</a></li></ul></div>\n<script type="text/javascript">\n    var data;\n    $.ajax({ url: "http://localhost:6001/base/home/skin/?type?/?index?", data: "", type: "POST", async: false, success: function (msg) { eval(msg); } });\n</script>\n<table cellspacing="0" cellpadding="0">\n <tfoot><tr><th><script type="text/javascript">pager(''?site("base/skin/home/{0}")?'',?index?,2,12);</script></th></tr></tfoot>\n<tbody>\n<tr style="background:#F6F6F6">\n<td>\n<ul id="skin"></ul>\n<script type="text/javascript">\n    var url = ''?site("")?'';\n    for (i = 0; i < data.list.length; i++) {\n        if (data.list[i].status == 0) {\n            ht = ''<li>'';\n        } else {\n            ht = ''<li class="cur">'';\n        }\n        ht += ''<img src="'' + data.list[i].pre + ''" height="200" width="180" title="'' + data.list[i].title + ''" alt="'' + data.list[i].title + ''"><br />'';\n        if (data.list[i].status == 0) {\n            ht += ''<span><a href="#" onclick="del(\\'''' + url + ''base/skin/set/'' + data.list[i].id + ''/1\\'',\\''jump\\'')">启用</a></span>'';\n        } else {\n            ht += ''<span class="red">使用中</span>'';\n        }        \n        ht += ''<span><a href="'' + data.list[i].detail + ''" target="_blank">预览</a></span></li>'';\n        $(''#skin'').append(ht);\n    }\n</script>\n</td></tr>\n</tbody>\n</table>\n?template("base_foot")?', '1002032132328300875', 1324201730, 1324225790, '1002032132328300875', 5, ''),
('base_template_design', '<form id="J_template" method="post" action="/base/template/edit" target="editor">\n    <select name="_type">\n        <option value="Add">Insert</option>\n        <option value="Edit">Update</option>\n        <option value="Home">List</option>\n    </select>\n    <input type="hidden" name="table" value="?name?" />\n    <input type="hidden" name="db" value="?db?" />\n    <input type="hidden" name="design" value="true" />\n    <button class="btn">生成</button>\n    <span class="btn" onclick="dbmap.coder()">列表</span>\n    <table>\n        <thead>\n        <tr>\n            <th>列名</th>\n            <th>域类型</th>\n            <th>验证</th>\n            <th>跳过</th>\n        </tr>\n        </thead>\n        <tbody>\n        <:foreach collection="?table.columns?" var="colunm">\n        <tr>\n            <td>?colunm.name?</td>\n            <td>\n                <select name="?colunm.name?">\n                    <option value="Text">Text</option>\n                    <option value="Hidden">Hidden</option>\n                    <option value="Password">Password</option>\n                    <option value="Select">Select</option>\n                    <option value="Textarea">Textarea</option>\n                    <option value="Checkbox">Checkbox</option>\n                    <option value="Radio">Radio</option>\n                </select>\n            </td>\n            <td><select name="?colunm.name?">\n                <option value="">请选择</option>\n                <option value="need">Need</option>\n                <option value="date">Date</option>\n                <option value="digit">Digit</option>\n                <option value="mail">Mail</option>\n                <option value="editor">Editor</option>\n            </select>\n            </td>\n            <td>\n                <input type="checkbox" name="?colunm.name?" value="jump">\n            </td>\n        </tr>\n        </:foreach>\n        </tbody>\n    </table>\n</form>', '100203213232830087