('base_template_edit', '<!DOCTYPE html>\n<html>\n<head>\n    <meta charset="UTF-8">\n    <title>Jabinfo Editor</title>\n    <link type="text/css" href="http://b.zento.me/base/css/dbmap.css" rel="Stylesheet"/>\n    <script src="/res/js/lib"></script>\n    <script src="http://b.zento.me/res/ace/src-min/ace.js"></script>\n    <script src="http://b.zento.me/res/ace/src-min/ext-language_tools.js"></script>\n<script>dbmap={};dbmap.iframe=true;</script>\n<script src="http://b.zento.me/base/js/page/dbmap.js"></script>\n</head>\n<body>\n<div class="title">\n    <label>名称</label>\n    <input type="text" id="J_title" name="title" value="?caption?">\n    <input type="hidden" id="J_templateId" value="?template_id?">\n    <select name="stype" id="J_type" koo="?type?">\n        <option value="5">后台</option>\n        <option value="6">后台公共模板</option>\n        <option value="8">前台</option>\n        <option value="7">前台公共模板</option>\n    </select>\n    <span id="J_button">保存</span>\n    <dl id="J_lang">\n        <dt>Snippets</dt>\n        <dd data-val="each">each</dd>\n        <dd data-val="if">if</dd>\n        <dd data-val="set">set</dd>\n        <dd data-val="else">else</dd>\n	<dd data-val="page">pager</dd>\n	<dd data-val="win">win</dd>\n    </dl>\n</div>\n<div id="J_tip"></div>\n<pre id="editor">?content?</pre>\n<script>dbmap.templateEditor()</script>\n</body>\n</html>\n\n', '1002032132328300875', 1265146913, 1416506502, '1002032132328300875', 5, ''),
('base_template_export', '?template(''base_head'')?\n<div id="postion"><a href="?site(''jabinfo012738'')?">桌面</a>模板列表</div>\n<div class="actions"><ul><li><a href="?site(''base/template/home'')?">模板列表</a></li>\n<li><a href="?site(''base/template/del_session'')?">清除列表</a></li>\n</ul></div>\n<table cellspacing="0" id="cur_body" cellpadding="0"><tr><th width="80%">模板名称</th><th width="20%">删除</th></tr>\n<tfoot><tr><td colspan="2"><form action="?site(''base/template/export'')?" method="post" target="_blank"><input type="hidden" id="current_obj" name="result" value="?result?" /><input type="submit" class="button1" value="导出" /></form></td></tr></tfoot>\n<tbody>\n<:foreach collection="?templates?" var="template">\n<tr>\n<td>?template?</td><td><a href="#" onclick="delresult(this,''?template?'')">删除</a></td>\n</tr>\n</:foreach>\n</tbody>\n</table>\n<script type="text/javascript">\nfunction delresult(obj,id)\n{\n	if(confirm(''确定吗？'')){\n		var val=$(''#current_obj'').val();\n		if($(obj).html().indexOf(''red'')>0){\n			$(''#current_obj'').val(val+id+'','');\n			$(obj).html(''删除'');\n		}else{\n			val=val.replace(id+'','','''');\n			$(''#current_obj'').val(val);\n			$(obj).parent().html(''<span style="color:red">已删除</span>'');\n		}\n	}\n}\n</script>\n?template(''base_foot'')?', '1103310937414229157', 1303409000, 1305728380, '1103310937414229157', 4, ''),
('base_template_import', '<tr><th>选择升级包</th><td><input type="file" name="file" /><input type="hidden" name="t" value="1" /></td></tr>', '1103310937414229157', 1303415550, 1328807810, '1002032132328300875', 4, ''),
('base_template_list', '<div class="search">\n    <input name="keyword" class="keyword" placeholder="请输入模版名称" type="text"/>\n    <span class="search_btn btn">搜索</span>\n    <a href="/base/template/edit" target="editor" class="btn">新建</a>\n</div>\n<table>\n    <thead>\n        <tr>\n            <th>名称</th>\n            <th>更新者</th>\n            <th>删除</th>\n        </tr>\n    </thead>\n    <tbody>\n    <:foreach collection="?templateList?" var="template">\n    <tr>\n        <td><a href="/base/template/edit/?template.template_id?" target="editor">?template.template_id?</a></td>\n        <td>?nick(template.update_uid)?</td>\n        <td><a href="/base/template/remove/?template.template_id?" class="del">删除</a></td>\n    </tr>\n    </:foreach>\n    </tbody>\n</table>', '1002032132328300875', 0, 1415113577, '1002032132328300875', 5, ''),
