('base_template_lost', '?template("base_head")?\n<div class="position"><a href="/jabinfo012738">桌面</a>系统回收站</div>\n<table cellspacing="0" cellpadding="0">\n    <tfoot><tr><th colspan="5"><script type="text/javascript">pager(''?site("base/template/lost/{0}")?'',?index?,?total?,?size?);</script></th></tr></tfoot>\n    <tbody>\n    <tr><th width="15%">编号</th><th width="30%">名称</th><th width="20%">时间</th><th width="20%">操作人</th><th width="15%">操作</th></tr>\n    <:foreach collection="?recycleList?" var="syslog">\n    <tr><td>?syslog.Logid?</td><td>?syslog.Title?</td>\n        <td>?date(syslog.Addtime)?</td>\n        <td>?nick(syslog.Uid)?-?username(syslog.Uid)?</td>\n        <td><a href="/base/template/recycle/?syslog.Logid?" onclick="del()">恢复</a></td>\n    </tr></:foreach>\n    </tbody>\n</table>\n?template("base_foot")?', '1103310937414229157', 1301683580, 1412874311, '1002032132328300875', 4, ''),
('base_template_password', '<tr><th>旧密码</th><td><input check="act-jump-need" type="password" name="password" /></td></tr>\r\n<tr><th>新密码</th><td><input type="password" name="newpassword" id="newpassword" /><input type="hidden" name="uid" value="?cookie(''uid'')?" /></td></tr>\r\n<tr><th>新密码</th><td><input type="password" name="pwd2" check="need-#newpassword" /></td></tr>', '1002032132328300875', 1265146913, 1359934000, '1002032132328300875', 4, ''),
