INSERT INTO `template` (`template_id`, `content`, `uid`, `addtime`, `update_time`, `update_uid`, `stype`, `module`) VALUES
('base_template_sql', '<!DOCTYPE html>\n<html>\n<head>\n    <meta charset="UTF-8">\n    <title>Jabinfo Editor</title>\n    <link type="text/css" href="http://b.zento.me/base/css/dbmap.css" rel="Stylesheet"/>\n    <script src="/res/js/lib"></script>\n    <script src="http://b.zento.me/res/ace/src-min/ace.js"></script>\n    <script src="http://b.zento.me/res/ace/src-min/ext-language_tools.js"></script>\n    <script>dbmap={};dbmap.iframe=true;</script>\n    <script src="http://b.zento.me/res/ace/src-min/ace.js"></script>\n    <script src="http://b.zento.me/res/ace/src-min/ext-language_tools.js"></script>\n    <style>\n        div,iframe,textarea{ime-mode:disabled}\n    </style>\n</head>\n<body id="iframe">\n    <form id="J_form">\n        <dl class="left">\n            <dt>方法类型</dt>\n            <dd>\n                <select name="resultType" id="J_type" koo="?sqlVO.resultType?">\n                    <option value="">请选择</option>\n                    <option value="select">Select</option>\n                    <option value="update">Update</option>\n                    <option value="insert">Insert</option>\n                    <option value="count">Count</option>\n                    <option value="delete">Delete</option>\n                    <option value="sum">Sum</option>\n                    <option value="ids">Ids</option>\n                    <option value="json">Json</option>\n                    <option value="row">Row</option>\n                    <option value="one">One</option>\n                </select>\n                <input type="hidden" id="J_sqlVal" name="sql" value="" />\n                <input type="hidden" name="dbname" value="?sqlVO.dbname?" />\n                <input type="hidden" name="tablename" value="?sqlVO.tablename?" />\n                <input type="hidden" id="sqlId" name="sqlId" value="?sqlVO.sqlId?">\n                &nbsp;<a href="/base/template/sql/?sqlVO.dbname?/?sqlVO.tablename?" class="btn">添加方法</a>\n            </dd>\n        </dl>\n        <dl class="left">\n            <dt>方法名称</dt>\n            <dd><input type="text" name="name" value="?sqlVO.name?">\n        </dl>\n        <dl class="left">\n            <dt>方法描述</dt>\n            <dd><input type="text" name="describe" value="?sqlVO.describe?" /></dd>\n        </dl>\n        <dl class="left" id="J_page" style="display:none">\n            <dt>开启分页</dt>\n            <dd><input type="checkbox" name="size" koo="?sqlVO.size?" value="1" /></dd>\n        </dl>\n        <dl id="J_field" class="left" value="?sqlVO.fields?">\n            <dt>字段列表</dt>\n            <dd style="width: 400px;">\n                <:foreach collection="?table.columns?" var="column">\n                <input type="checkbox" id="m_?column.name?" name="fields" value="?column.name?"><label for="m_?column.name?">?column.name?</label>\n                </:foreach>\n                <span id="J_insertField" style="display:none" class="btn">插入</span>\n            </dd>\n        </dl>\n        <dl class="left" id="J_text" style="display:none">\n            <dt>SQL语句</dt>\n            <dd><pre id="J_sql">?sqlVO.sql?</pre></dd>\n        </dl>\n        <dl class="left">\n            <dt>&nbsp;</dt>\n            <dd><span id="J_button">保存</span></dd>\n        </dl>\n    </form>\n    <div id="J_tip"></div>\n    <script src="http://b.zento.me/base/js/page/dbmap.js"></script>\n    <script>dbmap.sqlEditor("?sqlVO.tablename?","?sqlVO.resultType?")</script>\n</body>\n</html>\n\n', '1002032132328300875', 0, 1417883693, '1002032132328300875', 5, ''),
('base_template_sqlList', '<div class="search">\n    <span class="btn" onclick="dbmap.design(''?table?'')">模版设计</span>\n    <span class="btn" onclick="dbmap.func(''?table?'')">添加函数</span>\n    <!--<a href="/base/template/sql/?dbname?/?table?" target="editor" class="btn">添加函数</a>-->\n</div>\n<table>\n    <thead>\n        <tr>\n            <th>名称</th>\n            <th>备注</th>\n            <th>删除</th>\n        </tr>\n    </thead>\n    <tbody>\n    <:foreach collection="?funcs?" var="table">\n    <tr>\n        <td><a href="/base/template/sql/?table.sqlId?" target="editor">?table.name?-<em>?table.resultType?</em></a></td>\n        <td>?table.describe?</td>\n        <td><a href="/base/template/sqlDelete/?table.sqlId?" class="del">删除</a></td>\n    </tr>\n    </:foreach>\n    </tbody>\n</table>', '1002032132328300875', 0, 1416569973, '1002032132328300875', 5, ''),
('base_user_add', '<tr><th>用户名</th><td><input type="text" id="act-jump-need" name="username" /></td></tr>\n<tr><th>用户密码</th><td><input type="password" id="pwd" name="pwd" /></td></tr>\n<tr><th>确认密码</th><td><input type="password" id="pwd2-#pwd-need" name="pwd2" /></td></tr>\n<tr><th>昵称</th><td><input type="text" id="nick-need" name="nick" /></td></tr>', '1002032132328300875', 1265193980, 1267312175, '1002032132328300875', 4, ''),
('base_user_cache', '<tr><th>选择模块</th><td>\r\n<input type="checkbox" style="width:13px;" value="home,index001" name="path" />首页&nbsp;&nbsp;\r\n<:foreach collection="?cache.Keys?" var="key">\r\n<input type="checkbox" style="width:13px;" value="?key?" name="path" />?cache[key]?&nbsp;&nbsp;\r\n</:foreach>\r\n<input type="checkbox" style="width:13px;" value="all" name="path" />全部缓存</td></tr>\r\n<tr><td class="red" style="text-align:center" colspan="2">更新缓存后网站速度会变慢，5分钟后恢复正常，请勿频繁更新</td></tr>', '1002032132328300875', 1281797380, 1330082300, '1002032132328300875', 4, ''),
('base_user_home', '?template("base_head")?\n<div class="position"><a href="/jabinfo012738">桌面</a>用户列表</div>\n<div class="actions"><ul>\n    <li><a href="/base/user/add/0" onclick="mywin()">添加用户</a></li>\n    <li><a href="/base/user/search_view" onclick="mywin(''会员搜索'',''/base/user/search'')">会员搜索</a></li>\n    </ul>\n</div>\n<table cellspacing="0" cellpadding="0">\n    <tfoot><tr><th colspan="8"><script type="text/javascript">pager(''/base/user/home/{0}'',?index?,?total?,?size?);</script></th></tr></tfoot>\n    <tbody>\n    <tr><th style="width:15px"></th><th width="18%">用户名</th>\n    <th width="12%">团队ID</th>\n    <th width="18%">注册时间</th>\n    <th width="8%">登录次数</th>\n    <th width="18%">最后登录</th>\n    <th width="10%">类型</th><th width="15%">操作</th>\n    </tr>\n    <:foreach collection="?users?" var="u" forindex="?start?">\n    <tr><td>?forindex?</td>\n    <td>?u.Username?</td>\n    <td>?u.Email?</td>\n    <td>?idate(u.Uid)?</td>\n    <td>?u.Logins?</td>\n    <td>?date(u.Lasttime)?</td>\n    <td>?type[u.Rate]?</td>\n    <td>\n    <a href="/base/user/password/?u.Uid?" onclick="mywin(''重置?u.Username?的密码'')">重置密码</a> | \n    <a href="/base/user/remove/?u.Uid?" onclick="del()">删除</a></td></tr>\n    </:foreach>\n    </tbody>\n</table>\n?template("base_foot")?', '1002032132328300875', 1276791550, 1417883840, '1002032132328300875', 4, ''),
('base_user_password', '<tr><th>昵称</th><td><input type="text" name="nick" maxlength="10" /></td></tr>\n<tr><th>新密码</th><td><input type="password" id="newpassword098" name="newpassword" maxlength="30" /></td></tr>\n<tr><th>新密码</th><td><input type="password" id="pwd-need-#newpassword098" name="newpassword2" maxlength="50" /></td></tr>\n<tr><th>管理员密码</th><td><input type="password" id="password-need" name="password" maxlength="50" />\n<input type="hidden" name="uid" id="act-jump" value="?uid?" />\n</td></tr>', '1002032132328300875', 1276800900, 1307616130, '1002032132328300875', 4, ''),
('base_user_proccess', '<tr><th>执行时间</th><td><input type="text" name="date" id="time-date" />\n<input type="hidden" name="name" id="act-jump" value="?name?" />\n<span class="red">指定执行定时器的时间，空表示当前时间</span>\n</td></tr>', '1002032132328300875', 1318506500, 1318586750, '1002032132328300875', 4, ''),
('base_user_search', '?template("base_head")?\n<div id="postion"><a href="?site(''jabinfo012738'')?">桌面</a>用户列表</div>\n<div class="actions"><ul>\n<li><a href="/base/user/add/0" onclick="mywin()">添加用户</a></li>\n<li><a href="/base/user/search_view" onclick="mywin(''会员搜索'',''/base/user/search'')">会员搜索</a></li>\n</ul>\n</div>\n<table cellspacing="0" cellpadding="0">\n<tfoot><tr><th colspan="7"><script type="text/javascript">pager(''/base/user/search/{0}'',?index?,?total?,?size?);</script></th></tr></tfoot>\n<tbody>\n<tr><th width="15%">用户名</th><th width="15%">昵称</th>\n<th width="18%">邮箱</th>\n<th width="8%">登录次数</th>\n<th width="18%">最后登录</th>\n<th width="10%">类型</th><th width="15%">操作</th>\n</tr>\n<:foreach collection="?users?" var="u">\n<td>?u.Username?</td>\n<td>?u.Nick?</td>\n<td>?u.Email?</td>\n<td>?u.Logins?</td>\n<td>?date(u.Lasttime)?</td>\n<td>?type[u.Rate]?</td>\n<td>\n<a href="/base/user/password/?u.Uid?" onclick="mywin(''重置?u.Username?的密码'')">重置密码</a> | \n<a href="/base/user/remove/?u.Uid?" onclick="del()'')">删除</a></td></tr>\n</:foreach>\n</tbody>\n</table>\n?template("base_foot")?', '1002032132328303474', 1276859390, 1412866118, '1002032132328300875', 4, ''),
('base_user_search_view', '<tr><th>会员类型</th><td>\n<select name="rate">\n<option value="">请选择</option>\n<:foreach collection="?roles.Keys?" var="key">\n<option value="?key?">?roles[key]?</option>\n</:foreach>\n</select>\n</td></tr>\n<tr><th>用户名</th><td><input type="text" name="username" maxlength="32" /></td></tr>', '1002032132328300875', 1276801280, 1276801280, '1002032132328300875', 4, ''),
('base_user_task_add', '<tr><th>名称</th><td><input id="act-jump-char" type="text" value="" name="name" /></td></tr>\n<tr id="last_more"><th>类型</th><td>\n<select name="type" onchange="set_tk(this)">\n<option value="span">循环</option>\n<option value="day">天</option>\n<option value="week">星期</option>\n<option value="month">月</option>\n</select>\n</td></tr>\n<tr class="tkcnt"><th>间隔时间</th><td><input type="text" name="blunder" id="blunder-digit" /><span>(至少大于180)</span></td></tr>\n<tr><th>任务描述</th><td><textarea name="describe" id="describe-need" rows="6" cols="8"></textarea></td></tr>\n', '1106150918461672278', 1313518000, 1313574530, '1106150918461672278', 4, ''),
('basic_business_home', '?header("core_head")?\n<div id="postion"><a href="?site(''jabinfo012738'')?">桌面</a>用户列表</div>\n<div class="actions">\n<ul>\n<li><a href="#" onclick="mywin(''?site(''basic/business/add'')?'',''?site(''basic/business/add'')?'',''添加商业用户'')">用户搜索</a></li>\n</ul>\n</div>\n<table cellspacing="0" cellpadding="0">\n <tfoot><tr><th colspan="4"><script type="text/javascript">pager(''?site("basic/business/home/{0}")?'',?index?,?total?,?size?);</script></th></tr></tfoot>\n<tbody>\n<tr><th width="30%">用户编号</th><th width="20%">用户名</th><th width="20%">真实姓名</th><th width="30%">操作</th></tr>\n<:foreach collection="?businessList?" var="business">\n<tr>\n<td>?business.Uid?</td>\n<td>?username(business.Uid)?</td>\n<td>?business.Realname?</td>\n<td><a href="#" onclick="mywin(''?site(''basic/business/edit/?business.Uid?'')?'',''?site(''basic/business/edit'')?'',''编辑商业用户'')">编辑</a>\n | <a href="#" onclick="del(''?site(''basic/business/remove/?business.Uid?'')?'')">删除</a></td></tr>\n</:foreach>\n</tbody>\n</table>\n?template("base_foot")?', '1002032132328300875', 1323190140, 1323190910, '1002032132328300875', 5, ''),
('basic_feedback_home', '?header("core_head")?\r\n<div id="postion"><a href="?site(''jabinfo012738'')?">桌面</a>用户反馈</div>\r\n<div class="actions">\r\n<ul>\r\n<li>用户反馈列表</li>\r\n</ul>\r\n</div>\r\n<table cellspacing="0" cellpadding="0">\r\n <tfoot><tr><th colspan="7"><script type="text/javascript">pager(''?site("basic/feedback/home/{0}")?'',?index?,?total?,?size?);</script></th></tr></tfoot>\r\n<tbody>\r\n<tr><th width="3%"></th><th width="57%">描述</th><th width="15%">类型</th><th width="15%">反馈时间</th><th width="10%">操作</th>\r\n</tr>\r\n<:foreach collection="?feedbackList?" var="feedback" forindex="?start?">\r\n<tr>\r\n<td>?forindex?</td>\r\n<td style="text-align:left">?feedback.Content?  <a href="?attach(''?feedback.Feedback_id?0'')?" target="_blank">附1</a>  <a href="?attach(''?feedback.Feedback_id?1'')?" target="_blank">附2</a>  <a href="?attach(''?feedback.Feedback_id?2'')?" target="_blank">附3</a></td>\r\n<td>?jtype[feedback.Jtype]?</td>\r\n<td>?idate(feedback.Feedback_id)?</td>\r\n<td><a href="#" onclick="del(''?site(''basic/feedback/remove/?feedback.Feedback_id?'')?'')">删除</a></td></tr>\r\n</:foreach>\r\n</tbody>\r\n</table>\r\n?template("base_foot")?', '1201062002169631163', 1331200640, 1334048380, '1203161054563251176', 5, ''),
('basic_invite_add', '<tr><th>数量</th><td><input type="text" name="number" id="number-digit"></td></tr>', '1201062002169631163', 1330684000, 1330685440, '1201062002169631163', 5, ''),
('basic_invite_home', '?header("team_head")? \r\n<div id="postion"><a href="?site(''jabinfo012738'')?">桌面</a>邀请码列表</div>\r\n<div class="actions">\r\n<ul>\r\n<li><a href="?site(''basic/invite/home'')?">全部</a></li>\r\n<li><a href="?site(''basic/invite/home/0'')?">未使用</a></li>\r\n<li><a href="?site(''basic/invite/home/1'')?">已使用</a></li>\r\n<li><a href="?site(''basic/invite/home/2'')?">未发放</a></li>\r\n<li><a href="#" onclick="mywin(''?site(''basic/invite/add'')?'',''?site(''basic/invite/add'')?'',''生成'')">生成邀请码</a></li>\r\n<li><a href="#" onclick="mywin(''?site(''basic/invite/remove'')?'',''?site(''basic/invite/remove'')?'','' 回收'')">回收邀请码</a></li>\r\n</ul>\r\n</div>\r\n<table  width="100%" border="0" cellspacing="0" cellpadding="0">\r\n  <tfoot><tr><th colspan="10">\r\n     <script type="text/javascript">pager(''?site("basic/invite/home/?type?/{0}")?'',?index?,?total?,?size?);  \r\n     </script>\r\n  </th></tr></tfoot>\r\n  <tbody>\r\n  <tr>\r\n<th width="3%"></th>\r\n    <th width="17%" >邀请码</th>\r\n    <th width="20%">拥有者</th>\r\n    <th width="20%">受邀者</th>\r\n    <th width="40%">添加时间</th>\r\n  </tr>\r\n  <:foreach collection="?inviteList?" var="invite" forindex="?start?">  \r\n  <tr>\r\n<td>?forindex?</td><td>?invite.Invite_id?</td><td>?TeamTitle(invite.Team_id)?</td>\r\n<td>?date(invite.Addtime)?</td></tr> \r\n  </:foreach>\r\n</table>\r\n?template("base_foot")?  ', '1201062002169631163', 1330683520, 1335082110, '1203161054563251176', 5, ''),
('basic_invite_remove', '<tr><th>开始时间</th><td><input name="starttime" type="text" id="start-date"></td></tr>\r\n<tr><th>结束时间</th><td><input name="endtime" type="text" id="end-date"></td></tr>\r\n<input type="hidden" id="act-jump">', '1201062002169631163', 1330695550, 1330695550, '1201062002169631163', 5, ''),
('basic_shopper_addvip', '<tr><th>会员名</th><td><input name="keyword" id="keyword-need" type="text" style="width:150px"><a href="#" class="bt" onclick="up(''?site(''basic/shopper/getuser'')?'',''users'', ''keyword=''+$(''#keyword-need'').val())">查询</a></td></tr>\n<tr><th>选择会员</th><td><select id="users" name="uid" style="width:150px"><option value="">请选择会员</option></select></td></tr>\n<input type="hidden" id="act-jump">', '1201062002169631163', 1329317000, 1329317500, '1201062002169631163', 5, ''),
('basic_shopper_detail', '?header("shop_head")?\n<div id="postion"><a href="?site(''jabinfo012738'')?">桌面</a>会员详情</div>\n<div class="actions">\n<ul>\n<li>会员详情</li>\n</ul>\n</div>\n\n<table  width="100%" border="0" cellspacing="0" cellpadding="0">\n<tr><td width="10%" style="text-align:right;">会员名</td><td width="40%" style="text-align:left;">?username(shopper.Uid)?</td><td width="10%" style="text-align:right;">等级</td><td width="40%" style="text-align:left;">?getrate(shopper.Uid)?</td></tr>\n<tr><td width="10%" style="text-align:right;">昵称</td><td width="40%" style="text-align:left;">?nick(shopper.Uid)?</td><td width="10%" style="text-align:right;">荣誉</td><td width="40%" style="text-align:left;">?gethonour(shopper.Uid)?</td></tr>\n<tr><td width="10%" style="text-align:right;">邮箱</td><td width="40%" style="text-align:left;"></td><td width="10%" style="text-align:right;">累计消费金额</td><td width="40%" style="text-align:left;">?shopper.Total?</td></tr>\n<tr><td width="10%" style="text-align:right;">积分</td><td width="40%" style="text-align:left;">?shopper.Score?</td><td width="10%" style="text-align:right;">余额</td><td width="40%" style="text-align:left;">?shopper.Money?</td></tr>\n<tr><td width="10%" style="text-align:right;">地区</td><td width="40%" style="text-align:left;" colspan="3">?province? ?city? ?street?</td></tr><tr><td width="10%" style="text-align:right;">详细地址</td><td width="40%" style="text-align:left;" colspan="3">?shopper.Address?</td></tr>\n</table>\n?template("base_foot")?', '1201062002169631163', 1329329920, 1329332860, '1201062002169631163', 5, ''),
