('j_deal_home_confirm', '?template("j_shop_head")?\r\n <div class="main">\r\n<script type="text/javascript">\r\naddressjmp='''';\r\nfunction cnladdress()\r\n{\r\n	if(addressjmp=='''')\r\n		return false;\r\n	$(''#addressinfo'').html(addressjmp);\r\n	return false;\r\n}\r\nfunction setaddress(obj)\r\n{\r\n	addressjmp=$(''#addressinfo'').html();\r\n	if(obj==undefined)\r\n	{\r\n		up(''?site("deal/home/setaddress")?'',''addressinfo'');\r\n	}else if(obj.value!=''''){\r\n		up(''?site("deal/home/setaddress")?/''+obj.value,''addressinfo'');\r\n	}\r\n	return false;\r\n}\r\n\r\nfunction seticket()\r\n{\r\n	ticket_id=$(''#my_ticket'').val();\r\n	if(ticket_id=='''')\r\n	{\r\n		alert(''请输入优惠券号'');\r\n		return false;\r\n	}\r\n	up(''?site("deal/home/setticket")?/''+ticket_id,''!resulticket'');\r\n	return false;\r\n}\r\nfunction resulticket(msg)\r\n{\r\n	mg=msg.split(''$'');\r\n	if(mg.length<2)\r\n	{\r\n		alert(msg);\r\n	}else{\r\n		$(''#priceinfo'').html(mg[0]);\r\n		$(''#totalinfo'').html(mg[1]);\r\n		if(document.getElementById(''cnltickect'')==null)\r\n			$(''#ticket_info'').append(''&nbsp;<button id="cnltickect" class="bt" onclick="up(\\''?site("deal/home/setticket")?\\'',\\''!resultmsg\\'')">取消</button>'');\r\n	}\r\n}\r\nfunction resultmsg(msg)\r\n{\r\n	mg=msg.split(''$'');\r\n	if(mg.length<2)\r\n	{\r\n		alert(msg);\r\n	}else{\r\n		$(''#priceinfo'').html(mg[0]);\r\n		$(''#totalinfo'').html(mg[1]);\r\n	}\r\n}\r\n\r\nfunction setscore()\r\n{\r\n	score=parseInt($(''#my_score'').val());\r\n	if(isNaN(score)||score<1)\r\n	{\r\n		alert(''请输入正确的积分数量'');return false;\r\n	}\r\n$(''#my_score'').val(score);\r\n	up(''?site("deal/home/setscore")?/''+score,''!resultscore'');\r\n	return false;\r\n}\r\nfunction resultscore(msg)\r\n{\r\n	mg=msg.split(''$'');\r\n	if(mg.length<2)\r\n	{\r\n		alert(msg);\r\n	}else{\r\n		$(''#priceinfo'').html(mg[0]);\r\n		$(''#totalinfo'').html(mg[1]);\r\n		if(document.getElementById(''cnlscore'')==null)\r\n			$(''#score_info'').append(''&nbsp;<button class="bt" id="cnlscore" onclick="up(\\''?site("deal/home/setscore")?\\'',\\''!resultmsg\\'')">取消</button>'');\r\n	}\r\n}\r\nfunction setmoney()\r\n{\r\n	money=parseInt($(''#my_money'').val());\r\n	if(isNaN(money)||money<1)\r\n	{\r\n		alert(''请输入正确的金额'');return false;\r\n	}\r\n$(''#my_money'').val(money);\r\n	up(''?site("deal/home/setmoney")?/''+money,''!resultmoney'');\r\n	return false;\r\n}\r\nfunction resultmoney(msg)\r\n{\r\n	mg=msg.split(''$'');\r\n	if(mg.length<2)\r\n	{\r\n		alert(msg);\r\n	}else{\r\n		$(''#priceinfo'').html(mg[0]);\r\n		$(''#totalinfo'').html(mg[1]);\r\n		if(document.getElementById(''cnlmoney'')==null)\r\n			$(''#money_info'').append(''&nbsp;<button class="bt" id="cnlmoney" onclick="up(\\''?site("deal/home/setmoney")?\\'',\\''!resultmsg\\'')">取消</button>'');\r\n	}\r\n}\r\nfunction check()\r\n{\r\n}\r\nfunction setpay(msg)\r\n{\r\n	mg=msg.split(''$'');\r\n	$(''#netpayinfo'').html(mg[2]);\r\n	$(''#priceinfo'').html(mg[0]);\r\n	$(''#totalinfo'').html(mg[1]);\r\n}\r\n\r\n</script>\r\n    <div class="mycart_sub">\r\n    <div class="mycart_mygoodstit">填写核对订单信息</div>\r\n    <div class="mycart_mygoodsbg">\r\n    <div class="orderlist">\r\n  \r\n  \r\n  <form action="?site(''deal/home/setaddress'')?" id="act-addressinfo" method="post">\r\n  <h5><label>收货人信息：</label><a href="#reciever" name="reciever" onclick="up(''?site(''deal/home/setaddress'')?'',''addressinfo'')" class="red">[修改]</a>\r\n  <a href="#address" name="address" onclick="setaddress()" class="red">[其它地址]</a>\r\n  <span id="addresslist"></span>\r\n  </h5>\r\n  <dl id="addressinfo">\r\n	?address?\r\n  </dl>\r\n  </form>\r\n  \r\n  <form action="?site(''deal/home/setpay'')?" method="post" id="act-!setpay">\r\n  <h5>支付及配送方式：<a href="#deliver" name="deliver" onclick="up(''?site(''deal/home/setpay'')?'',''netpayinfo'')" class="red">[修改]</a></h5>\r\n  <dl id="netpayinfo">\r\n    ?deliver?\r\n  </dl>\r\n  </form>\r\n  \r\n  <form action="?site(''deal/home/setmessage'')?" method="post" id="act-messageinfo">\r\n  