on></span>(目前可用余额<span style="color:red;">?shopper.Money?</span>)\r\n	</div>\r\n	<a href="?site(''deal/home/finish'')?" onclick="return check()"><img src="?file(''res/shop/images/tijiaobiaodan.gif'')?" /></a></div>\r\n	<div style="clear:both"></div>\r\n  </div>\r\n  <div class="mycart_mygoodsbom"></div>\r\n </div>\r\n <div style="clear:both"></div>\r\n</div>\r\n?template("shop_foot")?', '1002032132328300875', 1289857660, 1332324480, '1201062002169631163', 8, '');
