('j_deal_home_confirm_to', '?template("shop_head")?\n <div class="main">\n\n<script type="text/javascript">\naddressjmp='''';\nfunction selectCompany(){\nvar bill_head = document.getElementsByName(''bill_head'');\nif(bill_head[1].id == ''10-10''){\n$(''#unitNameTr'').show();\n}\n}\nfunction sethead(setvalue)\n{\n  if(setvalue==5)\n  {\n    $(''#unitNameTr'').hide();\n  }\n  if(setvalue==10)\n {\n    $(''#unitNameTr'').show();\n }\n}\nfunction selectbill(setvalue)\n{\n  if(setvalue==0)\n  {\n    document.getElementById(''selectcontent'').style.display=''none'';\n  }\n  if(setvalue==1)\n {\n    document.getElementById(''selectcontent'').style.display='''';\n }\n}\nfunction cnladdress()\n{\n	if(addressjmp=='''')\n		return false;\n	$(''#addressinfo'').html(addressjmp);\n	return false;\n}\n\nfunction setaddress(obj)\n{\n	addressjmp=$(''#addressinfo'').html();\n	if(obj==undefined)\n	{\n		up(''?site("deal/home/setaddress")?'',''addressinfo'',''post'');\n	}else if(obj.value!=''''){\n		up(''?site("deal/home/setaddress")?/''+obj.value,''addressinfo'',''post'');\n            document.getElementById("addr_"+obj.value).checked="checked";\n	}\n	return false;\n}\n\nfunction seticket()\n{\n	var ticket_id=$(''#my_ticket'').val();\n	if(ticket_id=='''')\n	{\n		alert(''请输入优惠券号'');\n		return false;\n	}\n	up(''?site("deal/home/setticket")?/''+ticket_id,''!resulticket'');\n	return false;\n}\nfunction resulticket(msg)\n{\n	mg=msg.split(''$'');\n	if(mg.length<2)\n	{\n		alert(msg);\n	}else{\n		$(''#priceinfo'').html(mg[0]);\n		$(''#totalinfo'').html(mg[1]);\n		if(document.getElementById(''cnltickect'')==null)\n			$(''#ticket_info'').append(''&nbsp;<button id="cnltickect" class="bt" onclick="up(\\''?site("deal/home/setticket")?\\'',\\''!resultmsg\\'')">取消</button>'');\n	}\n}\nfunction resultmsg(msg)\n{\n	mg=msg.split(''$'');\n	if(mg.length<2)\n	{\n		alert(msg);\n	}else{\n		$(''#priceinfo'').html(mg[0]);\n		$(''#totalinfo'').html(mg[1]);\n	}\n}\n\nfunction setscore()\n{\n	var score=parseInt($(