('j_deal_member_address', '?template("j_shop_head")?\n<div class="main">\n    ?template("product_member_left")?\n    <div class="member_right">\n        <div class="member_daohang"><a href="?site('''')?">首页</a>&nbsp;&nbsp;>&nbsp;&nbsp;<a href="?site(''deal/member/index'')?">用户中心</a></div>\n        <div class="member_sub">\n        <div class="member_sub_tit">我的收货地址</div>\n        <div class="member_sub_co">\n			<table width="99%" border="0" cellspacing="0" cellpadding="0">\n				<:if test="?length lt 5?">\n				<tfoot><tr><td colspan="5"><a href="#" class="bt" onclick="address(''?site(''deal/member/editaddress'')?'')">添加新地址</a></td></tr></tfoot>\n				</:if>\n			  <tbody>\n			  <tr>\n				<th width="8%">联系人</th>\n				<th width="30%">联系电话</th>\n				<th width="8%">邮编</th>\n				<th width="40%">详细地址</th>\n				<th width="14%">操作</th>\n			  </tr>\n			  <:foreach collection="?address?" var="ad">\n			  <tr>\n				<td>?ad.Reciever?</td>\n				<td>?ad.Mobile?&nbsp;&nbsp;?ad.Telephone?</td>\n				<td>?ad.Postcode?</td>\n				<td>?ad.Pname?&nbsp;?ad.Cname?&nbsp;?ad.Sname?&nbsp;?ad.Address?</td>\n				<td><a href="#" onclick="address(''?site(''deal/member/editaddress'')?/?ad.My_address_id?'')" class="red">编辑</a>&nbsp;|&nbsp;<a href="#" onclick="del(''?site(''deal/member/remove_address'')?/?ad.My_address_id?'')" class="red">删除</a></td>\n			  </tr>\n			  </:foreach>\n			  </tbody>\n			</table>\n			<br />\n			<form action="?site(''deal/member/editaddress'')?" id="adform" method="post" style="display:none"></form>\n			<script type="text/javascript">\n			function address(url)\n			{\n			up(url,''adform'');\n			$(''#adform'').show();\n			}\n			</script>\n        </div>\n        <div style="clear:both"></div>\n        </div>\n        <div class="member_sub_bom"></div>\n    </div>\n    <div style="clear:both"></div>\n</div>\n?template("shop_foot")?', '1002032132328300875', 1289918720, 1311076350, '1103290918456919763', 8, ''),
