INSERT INTO `template` (`template_id`, `content`, `uid`, `addtime`, `update_time`, `update_uid`, `stype`, `module`) VALUES
('j_deal_member_detail', '?template("j_shop_head")?\r\n<div class="main">\r\n    ?template("product_member_left")?\r\n    <div class="member_right">\r\n        <div class="member_daohang"><a href="?site('''')?">首页</a>&nbsp;&nbsp;>&nbsp;&nbsp;<a href="?site(''deal/member/index'')?">用户中心</a>>&nbsp;&nbsp;<a href="?site(''deal/member/order'')?">订单列表</a></div>\r\n        <div class="member_sub">\r\n        <div class="member_sub_tit">订单信息</div>\r\n        <div class="member_sub_co">\r\n			<table width="99%" border="0" cellspacing="0" cellpadding="0">\r\n			  <tbody>\r\n			  <tr>\r\n				<td style="text-align:right" width="15%">订单号：</td>\r\n				<td style="text-align:left" width="35%">?order.Orders_id?</td>\r\n				<td style="text-align:right" width="15%">订单状态：</td>\r\n				<td style="text-align:left" width="35%">?status[order.Jstatus]?</td>\r\n			  </tr>\r\n			  <tr>\r\n				<td style="text-align:right">会员名：</td>\r\n				<td style="text-align:left">?username(order.Uid)?</td>\r\n				<td style="text-align:right">下单时间：</td>\r\n				<td style="text-align:left">?date(order.Addtime)?</td>\r\n			  </tr>\r\n			  <tr>\r\n				<td style="text-align:right">收货人：</td>\r\n				<td style="text-align:left">?order.Reciever?</td>\r\n				<td style="text-align:right">联系电话：</td>\r\n				<td style="text-align:left">?order.Mobile?&nbsp;&nbsp;&nbsp;?order.Telphone?</td>\r\n			  </tr>\r\n			  <tr>\r\n				<td style="text-align:right">收货地址：</td>\r\n				<td style="text-align:left" colspan="3">?pname?&nbsp;?cname?&nbsp;?sname?&nbsp;?order.Address?</td>\r\n			  </tr>\r\n			  <tr>\r\n				<td style="text-align:right">支付方式：</td>\r\n				<td style="text-align:left">?paytype[order.Pay_type]?</td>\r\n				<td style="text-align:right">送货时间：</td>\r\n				<td style="text-align:left">?sendtime[order.Sendtime]?</td>\r\n			  </tr>\r\n			  <tr>\r\n				<td style="text-align:right">物流公司：</td>\r\n				<td style="text-align:left" colspan="3">?deliver?&nbsp;&nbsp;\r\n<:if test="?order.Deliver_id not ''''?">\r\n发货时间：?date(order.Deliver_time)?&nbsp;&nbsp;快递号：?order.Deliver_id?<:else>(<span style="color:red">未发货</span>)</:if></td>\r\n			  </tr>\r\n			  </tbody>\r\n			</table>\r\n			<br />\r\n			<table width="99%" border="0" cellspacing="0" cellpadding="0">\r\n				<tfoot>\r\n				<tr><td colspan="4" style="font-weight:bold;text-align:right">商品金额：?order.Money?元 + 运费：?order.Psmoney?元 - 余额：?order.Balance?元 - 购物券：?order.Ticket_money?元 - 积分抵值：?order.Score_money?元 - 返现：?order.Rabate?元&nbsp;&nbsp;应付总额：<span style="color:red;">￥?order.Pay?</span>元&nbsp;<:if test="?order.Jstatus is 1?"></br><a href="?site(''deal/home/pay'')?/?order.Orders_id?" class="bt">现在去支付</a></:if>&nbsp;</td></tr>\r\n				</tfoot>\r\n			  <tbody>\r\n			  <tr>\r\n				<th>商品名称</th><th>购买数量</th><th>购买价</th><th>小计</th>\r\n			  </tr>\r\n			  <:foreach collection="?shops?" var="shop">\r\n			  <tr>\r\n				<td><a href="?site(''product/home/detail'')?/?shop.Product_id?/" target="_blank">?shop.Title?</a></td><td>?shop.Number?</td><td>?shop.Money?</td><td>?shop.Total?</td>\r\n			  </tr>\r\n			  </:foreach>\r\n			  </tbody>\r\n			</table>\r\n        </div>\r\n        <div style="clear:both"></div>\r\n        </div>\r\n        <div class="member_sub_bom"></div>\r\n    </div>\r\n    <div style="clear:both"></div>\r\n</div>\r\n?template("shop_foot")?', '1002032132328300875', 1289919100, 1329900160, '1201062002169631163', 8, ''),
