('j_product_home_index', '?template("j_shop_head")?\r\n<div class="main">\r\n    <div class="index_left">\r\n        <!--首页左侧-->\r\n        <div class="index_left">\r\n            <!--分类导航-->\r\n            <div class="index_club">\r\n                <div class="index_club_tit">分类导航</div>\r\n                <div class="co">\r\n<dl>\r\n	<:set name="catTop1" value="?api(''core'').Group(''1'')?" />\r\n	<:foreach collection="?catTop1?" var="ctop1">\r\n    <dt><a href="?site(''product/home/category'')?/?ctop1.Category_id?">?ctop1.Title?</a></dt>\r\n		<:set name="catTop2" value="?api(''core'').Group(ctop1.Category_id)?" />\r\n		<:foreach collection="?catTop2?" var="ctop2">\r\n		<dd><a href="?site(''product/home/category'')?/?ctop2.Category_id?" >?ctop2.Title?</a></dd>\r\n		</:foreach>\r\n	</:foreach>\r\n</dl>\r\n                <div style="clear:both"></div>\r\n                </div>\r\n            <div style="clear:both"></div>\r\n            </div>\r\n            <div><a href="?site(''product/home/detail/1007040857117037898'')?" target="_blank"><img src="?file(''res/shop/images/ad3.jpg'')?" /></a></div>\r\n        </div>\r\n		</div>\r\n        <!--首页左侧结束-->\r\n\r\n        <!--首页右侧-->\r\n        <div class="index_right">\r\n			<div style="overflow:hidden">\r\n				<div class="index_ad"><a href="?site(''product/home/detail/1007040857117037898'')?"><img src="?file(''res/shop/images/ad1.jpg'')?" /></a><div style="clear:both"></div></div>\r\n				<div class="main_block">\r\n				<div class="tit">商城公告</div>\r\n					<div class="main_gonggao">\r\n					<ul>\r\n					<:set name="articles" value="?api(''article'').category(''3273'',7)?" />\r\n					<:foreach collection="?articles?" var="article">\r\n						<li><a href="?site(''article/home/detail'')?/?article.Article_id?" title="?article.Title?" target="_blank">?article.Title?</a></li>\r\n					</:foreach>\r\n					</ul>\r\n					<div class="more"><a href="?site(''article/home/category/3273'')?">更多...</a></div>\r\n					</div>\r\n				</div>