	if(shownotice.showstr!=undefined)\n		{\n			alert(shownotice.showstr);\n		}else\n		{\n			alert(''你已提交过评论'');\n		}\n	}\n}\nshownotice.isshow=true;\nfunction addnoticefish(msg)\n{\n	var ishide=true;\n	var isreload=false;\n	if(msg!=''''&&msg!=undefined){\n		switch(msg)\n		{\n			case ''0'':alert("该缺货通知信息已添加，请勿重复添写");break;\n			case ''-1'':alert("数据出错，找不到你要添加的产品");isreload=true;break;\n			case ''-2'':alert("请先登陆用户");is_qingxinglogin=true;login(notice_submit);break;\n			case ''-3'':alert("请提交登记数据");ishide=false;break;\n            case ''-4'':alert(''手机号码不正确，请重新添写'');ishide=false;break;\n            case ''-5'':alert("Email格式不正确，请重新添写");ishide=false;break;\n			case ''-6'':alert(''手机号码和Email两者必须添写一个'');ishide=false;break;\n			case ''-7'':alert(''产品不处于缺货状态'');isreload=true;break;\n			case ''-8'':alert(''找不到产品数据'');isreload=true;break;\n			case ''-8'':shownotice.showstr=''该产品你已添加过缺货登记信息，请不要多次添加'';alert(shownotice.showstr);shownotice.isshow=false;break;\n			case ''1'':/*shownotice.showstr="该产品你已添加过缺货登记信息，请不要多次添加";shownotice.isshow=false;*/alert("缺货登记成功");break;\n			default:alert(msg);isreload=true;break;\n		}	\n	}\n	if(ishide){$(''#notice_div'').hide();\n	}\n	if(isreload){window.location.reload();}\n	return true;\n}\nfunction notice_submit()\n{\n	showaddnotice_div();\n	if(notice_button_click())\n	{\n	formdata=$(''#noticeform_luda'').serialize();\n	$.ajax({\n		type:''POST'',\n		url: $(''#noticeform_luda'').attr(''action''),\n		data: formdata,\n		async: false,\n		error: function(datas){\n			alert("请求数据出错");location.href="/";\n			return true;\n		},\n		success:addnoticefish});\n	}\n}\nfunction notice_button_click()\n{\n	var mailvale=$.trim($(''#notice_email'').val());\n    var is_nullmail=false;\n    if(mailvale==''''){\n        is_nullmail=true;\n    }else{\n		var n_t_b = /^\\w+([-+.]\\w+)*@\\w+([-.]\\w+)*\\.\\w+([-.]\\w+)*$/ ;\n		if(mailvale.length>35){\n			alert(''邮箱过长，不能使用'');\n			return false;\n		}\n		if(!mailvale.match(n_t_b)){\n			 alert(''请输入正确的邮箱'');\n			return false;\n		}\n	}\n    var telpval=$.trim($(''#notice_telphone'').val());\n    if(telpval==''''){\n		if(is_nullmail)\n		{\n		alert(''邮箱和手机号两者必须添一个'');\n		return false;\n		}        \n    }\n	else{\n		var n_t_a = /^((?keyword()?:0\\d{2}[-]*?keyword()?\\d{8})|(?keyword()?:0\\d{3}[-]*?keyword()?\\d{7,8})|(?keyword()?:(?keyword()?:\\+86)*?keyword()?1[3458]\\d{9})){1}$/ ;\n		if(telpval.length!=11||!telpval.match(n_t_a) ){\n			alert("请输入正确的手机号码或电话号码\\n\\n例如:13916752109或0712-3614072");\n			return false;\n		}\n	}\n	$(''#notice_email'').val(mailvale);\n    $(''#notice_telphone'').val(telpval);\n	return true;\n}\n\n</:if>\n\n\n\n/*团购处理函数*/\nvar istuan=false;/*不存在团购*/\nvar tuanVO=null;\n\nvar interval_id=null;\nvar nowtime=0;\nvar lasttime=0;\n\n	function tuan_timeinit()\n	{\n	    \n		if(tuanVO==null){return false;}else{istuan=true;}\n                if(tuanVO.jtype!=8){return false;}\n		if(tuanVO.jstatus==''9''){return false;}\n		if(tuanVO.is_over=="0")\n		{\n			if(tuanVO.latest<1)\n			{\n				return false;\n			}\n		}\n		if(nowtime>tuanVO.starttime&&nowtime<tuanVO.starttime+tuanVO.timelength)\n		{\n			lasttime =tuanVO.starttime+tuanVO.timelength-nowtime;\n			interval_id=setInterval("show_time()", 1000);\n			show_time();\n			$(''#tuan_price'').html("<b>￥</b>"+tuanVO.price);$(''#tuan_current'').html(tuanVO.current);\n			$(''.tuan_ul'').show();\n			return true;\n		}else if(nowtime<tuanVO.starttime)\n		{\n			setTimeout(tuan_start,(tuanVO.starttime-nowtime)*1000);\n			return true;\n		}return false;\n	}\n	function tuan_start()\n	{\n		interval_id=setInterval("show_time()", 1000);\n		lasttime=tuanVO.timelength;\n		show_time();\n		$(''#tuan_price'').html("<b>￥</b>"+tuan_price);$(''#tuan_current'').html(tuanVO.current);\n		$(''.tuan_ul'').show();\n	}\n	function hide_time()\n\n	{\n		$(''.tuan_ul'').hide();\n	}\n    function show_time()\n    {\n		if(!lasttime>1){clearInterval(interval_id);hide_time();return false;}\n		lasttime=lasttime-1;\n        var houre=Math.floor(lasttime/3600);\n        var minites=Math.floor((lasttime%3600)/60);\n        var seconds=lasttime%60;\n        if(houre<10){\n            houre=''0''+houre;\n        }\n        if(minites<10){\n            minites=''0''+minites;\n        }\n        if(seconds<10){\n            seconds=''0''+seconds;\n        }\n        $(''#id_Houre'').text(houre);\n        $(''#id_Minites'').text(minites);\n        $(''#id_Seconds'').text(seconds);\n    }\n/*浏览记录展现函数*/\nfunction show_viewproduct(msg)\n{\n	if(msg!=""&&msg.length>0){\n				var listr="";\n				for(var i=0;i<msg.length;i++){\n					listr+="<li><ul class=\\"pro_desgoodlistinfo\\"><li>";\n					listr+="<a href=\\"?site(''products'')?/"+msg[i].product_id+"\\" title=\\""+msg[i].title+msg[i].caption+"\\"><img alt=\\""+msg[i].title+msg[i].caption+"\\" src=\\""+msg[i].img.replace("_60","_160")+"\\"  width=\\"120\\" height=\\"120\\" /></a></li>";\n					listr+="<li><a href=\\"?site(''products'')?/"+msg[i].product_id+"\\" title=\\""+msg[i].title+msg[i].caption+"\\"  target=\\"_blank\\">"+msg[i].title.substring(0,10)+"</a></li>";\n					listr+="<li>会员价:<b class=\\"colc00\\">￥"+msg[i].price+"</b></li>";\n					listr+="</ul></li>";\n				}\n		        $(''#viewproducts1212'').html(listr);\n		    }\n            else{\n			$(''#viewproducts1212'').html("<li>暂时还没有浏览其它产品</li>");\n		    }\n}\n\n/*异步加载页面所需数据函数*/\n	function viewlist(){\n	    $.ajax({\n		type:''get'',\n        cache:false,\n		async: false,\n		url:''?site(''product/home/detaildata_json'')?/?product_id?/?color_id?/10'',\n		dataType:''json'',\n		error: function(datas){\n		  $(''#viewproducts1212'').html("<li>读取数据出错，请刷新页面重试</li>"); return true;\n		},\n		success:function(pagedata){\n			var msg=pagedata[0];\n		\n			nowtime=msg.nowtime;\n			if(msg.tuan.length>0)\n			{\n				tuanVO=msg.tuan[0];\n			}\n			tuan_timeinit();\n			\n			show_viewproduct(msg.viewproducts);\n			$(''#pro_eva'').removeClass("hide");			\n		}\n	    });\n	}\n	$(window).load(viewlist);\n</script>\n<script type="text/javascript" src="?file(''resource/js/res_login.js'')?"></script>\n</div>\n?template(''product_foot'')?', '1104110956576135682', 1304416000, 1317290240, '1105260920487534578', 8, ''),
('j_product_home_noinstore', '<tr><th>通知手机</th><td><input name="telphone" id="tel-tel" type="text"></td></tr>\n<tr><th>通知邮箱</th><td><input name="email" id="email-mail-need" type="text"></td></tr>\n<input name="product_id" type="hidden" value="?product_id?" id="act-jump">', '1201062002169631163', 1329499390, 1329500540, '1201062002169631163', 5, ''),
('j_product_home_notice_tanchu', '<div class="logtancss" id="notice_div" style="display:none;">\n<div class="logtan" style="width:300px; height:250px; background-color:#F7F7F7">\n<form action="?site(''product/home/add_notice'')?" method="post" id="noticeform_luda" >\n	<h3 style="border:0px; background:none; height:35px; width:300px; line-height:35px;"><span style="color:#333;"><em style="color:#F60; font-size:20px;">●</em>&nbsp;&nbsp;缺货通知</span><p onclick="$(''#notice_div'').hide();" title="关闭" ></p></h3>\n    <div class="logtaninfo" style="width:280px;">\n    	<dl style="line-height:20px; margin-bottom:0px; margin-top:0px;">\n        请留下您的联系方式，如果该商品到货，我们会第一时间通过邮箱或者手机短信通知您。\n        </dl>\n    	<dl style="margin-bottom:0px;">\n        	<dt style="width:55px;">Email：</dt>\n            <dd><input name="email" id="notice_email" class="logtaninp1" type="text" /></dd>\n        </dl>\n    	<dl style="margin-bottom:0px;">\n        	<dt style="width:55px;">手机号：</dt>\n            <dd><input name="telephone" id="notice_telephone" class="logtaninp1" type="text" title="Email和电话至少输入一个" /></dd>\n        </dl>\n    	<dl style="margin-bottom:0px;">\n        	<dt></dt>\n            <dd><input name="" type="submit" class="logtaninp4" value="" id="noticebutton_luda" onclick="return notice_button_click();" /><input type="hidden" id="act-!addnoticefish" /></dd>\n        </dl>\n    </div>\n</form>\n</div>\n</div>', '1103221450506106769', 1309952510, 1309965000, '1103221450506106769', 6, ''),
('j_product_home_overtuan', '<:set name="title" value="好开门团购 - 天天精品|给力团购|支持货到付款的团购"/>\n?template("article_home_head")?\n<script type="text/javascript">\n	$(function (){\n	    $("#menu li a").eq(4).attr(''class'',''menulii'');\n	    $("#menu li a").eq(4).css({"color":"#000","text-decoration":"none"});\n	});\n</script>\n<div id="pro_view">\n	<h2 class="hide">今日惊爆内容</h2>\n	<div class="pro_jingtitold"><a href="?site(''product/home/tuanlist'')?">今日惊爆</a><span class="sp">往期团购</span></div>\n    <div class="pro_jingbox">\n	<:if test="?isnull(tuanlist)?">\n	<:else>\n    <:foreach collection="?tuanlist?" var="tuan">\n	<:if test="?isnull(tuan)?">\n	<:else>\n    	<div class="pro_jingli">\n        	<ul>\n            	<li class="jl1"></li>\n            	<li class="jl2">\n					<a href="?site(''product/home/tuan/1'')?/?tuan.Tuan_log_id?" target="_blank" title="?tuan.Tuantitle?" >?tuan.Tuantitle?</a>\n				</li>\n            	<li class="jl3">\n					<a href="?site(''product/home/tuan/1'')?/?tuan.Tuan_log_id?" target="_blank" title="?tuan.Tuantitle?">\n					<img src="?attach(''?tuan.Product_id?_240'')?" alt="?tuan.Tuantitle?" width="240" height="240" />\n					</a>\n				</li>\n            	<li class="jl4">\n                <span class="js1">原价:<em><s>￥?money(tuan.Market_price)?</s></em></span>\n                <span class="js2">折扣:<em>?tuan.Discount?</em>折</span>\n                <span class="js3"><em>?tuan.Current_number?</em>人购买</span>\n                </li>\n            	<li class="jl6" id="tuan_foot?tuan.Product_id?" >\n                <p class="pj1"><span>￥?money(tuan.Price)?</span></p>\n                <p class="pj2"><a href="?site(''product/home/tuan/1'')?/?tuan.Tuan_log_id?" title="立刻购买" target="_blank">我要买</a></p>\n                </li>\n            </ul>\n        </div>\n    </:if>\n	</:foreach>\n	</:if>   \n    </div>\n    \n    \n    <div class="jing_page">\n      <ul>\n        <li class="proclass_useli5">?pager(''product/home/overtuan/{0}'',index,total,size)?</li>\n      </ul>\n    </div>\n    \n    \n</div>\n?template(''foot'')?', '1103221450506106769', 1311361000, 1315305470, '1108101910013779080', 8, '');
