('j_product_home_productmsg', '<:set name="title" value="?product.Title?-评论-?title?" />\n?template("article_home_head")?\n<script type="text/javascript">\n$(function(){\n  var type=''?type?'';\n  switch(type){\n     case ''1'':\n        $(''#comm_contit1'').attr("class","comm_rtit2");\n        break;\n     case ''2'':\n        $(''#comm_contit2'').attr("class","comm_rtit2");\n        break;\n     case ''3'':\n        $(''#comm_contit3'').attr("class","comm_rtit2");\n        break;\n     case ''4'':\n        $(''#comm_contit4'').attr("class","comm_rtit2");\n        break;\n  }\n});\n\n $(document).ready(function() {\n    $(".shortword").each(function() {\n       var inText = $(this).text();\n       if(inText.length > 9) {\n         $(this).text(inText.substr(0, 9) + "...")\n       }\n    });\n });\n</script>\n<div id="about_view">\n   <div class="about_tit">\n   <a href="?site(''product/home/index'')?" title="首页">首页</a> ><a href="?site(''products'')?/?product.Product_id?" title="?product.Title?">?product.Title?</a> >商品评论\n   </div>\n\n   <div class="comm_left">\n      <h3>商品信息</h3>\n      <div class="comm_linfo">\n       	 <ul>\n            <li class="center"><a href="?site(''products'')?/?product.Product_id?" target="_blank" title="?product.Title?"><img src="?attach(''?product.Product_id?_160'')?" width="160" height="160" alt="?product.Title?" /></a></li>\n            <li>商品名称：<a href="?site(''products'')?/?product.Product_id?" target="_blank" title="?product.Title?">?product.Title?</a></li>\n            <li>好开门价：<b class="colc00">￥?product.Price?</b></li>\n            <li><span>评价得分：</span><span class="comm_x_?product.Star_title?"></span><span>(?product.Star_title?颗星)</span></li>\n            <li>评论数：?totalAll?条</li>\n            <li>\n               <form id="act-jump" action="?site(''deal/home/addproduct'')?" method="post">\n                  <input type="hidden" name="number" value="1" id="number" />\n                  <input type="hidden" id="product_id" name="product_id" value="?product.Product_id?" />\n                  <input name="" class="zxbuyinp" type="submit" value="立即购买" />\n               </from>\n            </li>\n         </ul>\n      </div>\n   </div>\n\n   <div class="comm_right">\n      <div class="comm_rtit">\n         <ul>\n            <li class="comm_rtit1" id="comm_contit1"><a href="?site(''product/home/productmsg'')?/?product.Product_id?/1">全部评论(?totalAll?)</a></li>\n            <li class="comm_rtit1" id="comm_contit2"><a href="?site(''product/home/productmsg'')?/?product.Product_id?/2">好评(?totalGood?)</a></li>\n            <li class="comm_rtit1" id="comm_contit3"><a href="?site(''product/home/productmsg'')?/?product.Product_id?/3">中评(?totalNormal?)</a></li>\n            <li class="comm_rtit1" id="comm_contit4"><a href="?site(''product/home/productmsg'')?/?product.Product_id?/4">差评(?totalPoor?)</a></li>\n         </ul>\n      </div>\n\n      <!-- 评价 开始-->\n      <div class="comm_rinfo" id="comm_conID1">\n         <:set name="hon" value="?config(''core.honour'')?" />\n         <:foreach collection="?messageList?" var="msg">\n         <div class="comm_rinfobox">\n            <div class="comm_ruser">\n               <ul>\n<li><a href="#" title="?username(msg.Uid)?"><img src="?file(''resource/img/vipic?honour(msg.Uid)?.jpg'')?" width="60" height="60" alt="?username(msg.Uid)?" /></a></li>\n                        <li><a href="#" title=“?nick(msg.Uid)?”>?nick(msg.Uid)?</a></li>\n        </ul>\n            </div>\n            <div class="comm_rinfotext">\n               <div class="comm_rtexttop">\n                  <p class="comm_rtexttopleft">\n                     <span>?msg.Title?</span>\n                     <span class="comm_x_?msg.Star1?"></span>\n                     <em class="colc00">?msg.Star1?</em><em class="comm_fen">分</em>\n                  </p>\n                  <p class="comm_rtexttopright comm_fen">?date(msg.Addtime)?</p>\n               </div>\n               <div class="comm_rtextcen">\n