?tuan.Is_over?;\nvar jtype=?tuan.Jtype?;\nvar latest=?tuan.Latest?;\n	function tuan_timeinit()\n	{\n		if(!istuan){return false;}\n		if(tuan_status==''9''){return false;}\n                if(is_over=="0")\n		{\n			if(jtype==1||latest<1)\n			{\n				$(''#check_tuan_over'').removeClass(''ct'').addClass(''tend'');\n				$(''#check_tuan_over_but'').remove();\n				$(''.time'').html(''<p style="background:none;" class="size14">本团购已结束。</p>'');\n$(''.goon'').addClass(''ds3'').html(''<p><span class="ws1">共有<span class="ws2">?tuan.Current_number?</span>人购买了</span></p>'');\n				return false;\n			}\n		}\n		if(nowtime>starttime&&nowtime<starttime+tuanhouretime)\n		{\n			lasttime =starttime+tuanhouretime-nowtime;\n			interval_id=setInterval("show_time()", 1000);\n			show_time();\n			$(''#tuan_price'').html("<b>￥</b>"+tuan_price);$(''#tuan_current'').html(tuan_current);\n			$(''.tuan_ul'').show();\n			return true;\n		}else if(nowtime<starttime)\n		{\n			setTimeout(tuan_start,(starttime-nowtime)*1000);\n			return true;\n		}return false;\n	}\n	function tuan_start()\n	{\n		interval_id=setInterval("show_time()", 1000);\n		lasttime=tuanhouretime;\n		show_time();\n		$(''#tuan_price'').html("<b>￥</b>"+tuan_price);$(''#tuan_current'').html(tuan_current);\n		$(''.tuan_ul'').show();\n	}\n	function hide_time()\n	{\n		$(''.tuan_ul'').hide();\n	}\n    function show_time()\n    {\n		if(!lasttime>1){clearInterval(interval_id);hide_time();return false;}\n		lasttime=lasttime-1;\n		var day=Math.floor(this.lasttime/(24*3600));\n        var houre=Math.floor((this.lasttime%(24*3600))/3600);\n        var minites=Math.floor((this.lasttime%3600)/60);\n        var seconds=this.lasttime%60;\n        if(houre<10){\n            houre=''0''+houre;\n        }\n        if(minites<10){\n            minites=''0''+minites;\n        }\n        if(seconds<10){\n            seconds=''0''+seconds;\n        }\n		$(''#id_Day'').text(day);\n        $(''#id_Houre'').text(houre);\n        $(''#id_Minites'').text(minites);\n  