		$(''#order2'').attr(''class'',''proclass_xb'');\n				if(url[1] == ''4'')\n					$(''#order4'').attr(''class'',''proclass_tt'');\n\n			}\n		}\n	}\n});\n</script>\n<div id="pro_view">\n	<h2 class="hide">今日惊爆内容</h2>\n	<div class="pro_jingtit"><span class="sp">今日惊爆</span><a href="?site(''product/home/overtuan'')?">往期团购</a></div>\n	<div class="proclass_class t_classbox">\n    <div class="proclass_classbox" style="width:100%;">\n			<!--品牌排序-->\n			<:set name="brands" value="?api(''core'').Tags_brand(''2911'')?" />	\n			<:if test="?isnull(brands)?">\n			<:else>\n            <dl>\n                <dt>品牌:</dt>\n                <dd style="width:890px;">\n                    <ul>\n                    	<li id="brand_0"  class=""><a href="?site(''product/home/tuanlist'')?/?tag_url(current,''price'',''?brand_id?'')?">全部</a></li>\n                		<:foreach collection="?brands?" var="brand">\n						<li id="brand_?brand.Brand_id?" class="proclass_classout"><a href="?site(''product/home/tuanlist'')?/?brand.Brand_id?/0/?tag_url(current,''brand'',brand.Brand_id)?">?brand.Title?</a></li>\n						</:foreach>\n				    </ul>\n                </dd>\n            </dl>\n			</:if>\n			<!--品牌排序结束-->\n			\n			<!--价格排序-->\n			<:set name="prices" value="?api(''core'').Tag_price(''2911'')?" />\n			<:if test="?isnull(prices)?">\n			<:else>\n            <dl>\n                <dt>价格:</dt>\n                <dd style="width:890px;">\n                    <ul>\n                        <li id="price_0x0" class=""><a href="?site(''product/home/tuanlist'')?/?brand_id?/0/?tag_url(current,''price'',''0x0'')?">全部</a></li>\n						<:foreach collection="?prices.Keys?" var="price">\n						<li id="price_?price?" class="proclass_classout"><a href="?site(''product/home/tuanlist'')?/?brand_id?/0/?tag_url(current,''price'',price)?">?prices[price]?</a></li>\n						</:foreach>\n                  </ul>\n                </dd>\n            </dl>\n			</:if>\n			<!--价格排序结束-->\n		</div>\n  </div>  \n    <div class="pro_jingbox">\n	<:if test="?isnull(tuanlist)?">\n	<:else>\n    <:foreach collection="?tuanlist?" var="tuan">\n	<:if test="?isnull(tuan)?">\n	<:else>\n    	<div class="pro_jingli">\n        	<ul>\n            	<li class="jl1"></li>\n            	<li class="jl2">\n					<a href="?site(''product/home/tuan'')?/?tuan.Product_id?/0" target="_blank" title="?tuan.Tuantitle?" >?tuan.Tuantitle?</a>\n				</li>\n            	<li class="jl3">\n					<a href="?site(''product/home/tuan'')?/?tuan.Product_id?/0"  target="_blank" title="?tuan.Tuantitle?">\n					<img src="?attach(''?tuan.Product_id?_240'')?" alt="?tuan.Tuantitle?" width="240" height="240" />\n					</a>\n				</li>\n            	<li class="jl4">\n                <span class="js1">原价:<em><s>￥?money(tuan.Market_price)?</s></em></span>\n                <span class="js2">折扣:<em>?tuan.Discount?</em>折</span>\n                <span class="js3"><em>?tuan.Current_number?</em>人购买</span>\n                </li>\n            	<li class="jl5" id="tuan_foot?tuan.Product_id?" >\n                <p class="pj1"><span>￥?money(tuan.Price)?</span>&nbsp;<em id="d_num?tuan.Product_id?">0</em>天<em  id="h_num?tuan.Product_id?">0</em>小时<em  id="i_num?tuan.Product_id?">0</em>分<em  id="s_num?tuan.Product_id?">0</em>秒</p>\n                <p class="pj2"><a href="?site(''product/home/tuan'')?/?tuan.Product_id?/0" title="立刻购买" target="_blank">我要买</a></p>\n                </li>\n            </ul>\n        </div>\n    </:if>\n	</:foreach>\n	</:if>   \n    </div>\n    \n    \n    <div class="jing_page">\n      <ul>\n        <li class="proclass_useli5">?pager(''product/home/tuanlist/?brand_id?/{0}/?current?'',index,total,size)?</li>\n      </ul>\n    </div>\n    \n    \n</div>\n<script type="text/javascript">\nvar nowtime=0;\nvar interval_id=null;\nfunction load_nowtime(){\n	    $.ajax({\n		type:''get'',\n        cache:false,\n		async: false,\n		url:''?site(''product/home/get_nowtime'')?'',\n		error: function(datas){\n			alert(''获取时间出错，请刷新页面重试''); return;\n		},\n		success:function(pagedata){\n			nowtime=pagedata;\n			interval_id=setInterval(tuan_showtime, 1000);\n			tuan_timeinit();\n		}\n	    });\n	}\nfunction tuanvo(product_id,starttime,timelength,price)\n{\n	this.product_id=product_id;\n	this.starttime=starttime;\n	this.timelength=timelength*3600;\n	this.lasttime=0;\n	this.price=price;\n	this.stop=false;\n	this.hide_time=function()\n	{\n		this.stop=true;\n		$(''#tuan_foot''+this.product_id).html(''<p class="pj1"><span>￥''+this.price+''</span></p><p class="pj2"></p>'');\n		$(''#tuan_foot''+this.product_id).removeClass("jl5").addClass("jl6");\n		/*remove_tuan(this.product_id);*/\n	};\n    this.show_time=function()\n    {\n		if(!this.lasttime>1){this.hide_time();return false;}\n		this.lasttime=this.lasttime-1;\n		var day=Math.floor(this.lasttime/(24*3600));\n        var houre=Math.floor((this.lasttime%(24*3600))/3600);\n        var minites=Math.floor((this.lasttime%3600)/60);\n        var seconds=this.lasttime%60;\n		$(''#d_num''+this.product_id).text(day);\n        $(''#h_num''+this.product_id).text(houre);\n        $(''#i_num''+this.product_id).text(minites);\n        $(''#s_num''+this.product_id).text(seconds);\n    };\n}\nvar tuanlist=new Array();\n<:if test="?isnull(tuanlist)?">\n<:else>\n<:foreach collection="?tuanlist?" var="tuan" forindex="0" >\n<:if test="?isnull(tuan)?">\n<:else>\ntuanlist[?forindex?-1]=new tuanvo(''?tuan.Product_id?'',?tuan.Starttime?,?tuan.Length?,?tuan.Price?);\n</:if>\n</:foreach>\n</:if>\n\n	function tuan_showtime()\n	{		\n		for(var i=0;i<tuanlist.length;i++)\n		{\n			if(!tuanlist[i].stop){tuanlist[i].show_time();}\n		}\n		if(!tuanlist.length)\n		{\n			clearInterval(interval_id);\n		}\n	}\n	function tuan_timeinit()\n	{\n		var isstart=false;	\n		for(var i=0;i<tuanlist.length;i++)\n		{\n			if(nowtime>tuanlist[i].starttime&&nowtime<tuanlist[i].starttime+tuanlist[i].timelength)\n			{\n				tuanlist[i].lasttime =tuanlist[i].starttime+tuanlist[i].timelength-nowtime;\n				tuanlist[i].show_time();\n				isstart=true;\n			}\n			else\n			{\n				tuanlist[i].hide_time();\n			}\n		}\n		if(!isstart)\n		{\n			clearInterval(interval_id);\n		}\n	}\n	$(load_nowtime());\n</script>\n?template(''foot'')?', '1103221450506106769', 1306417660, 1315938690, '1108101910013779080', 8, '');
