('product_product_add', '?template("product_head")?\n<div class="position">\n<a href="?site(''jabinfo012738'')?">桌面</a><a href="?site(''product/product/home'')?">商品列表</a>\n添加商品</div>\n<div class="actions">\n<ul>\n<li>基本信息</li>\n<li onclick="tip()">商品介绍</li>\n<li onclick="tip()">商品品牌</li>\n<li onclick="tip()">商品相册</li>\n<li onclick="tip()">库存管理</li>\n<li onclick="tip()">搭配赠品</li>\n<li onclick="tip()">最佳拍档</li>\n<li>产品日志</li>\n</ul>\n</div>\n<form action="?site(''product/product/add'')?" koo="act-jump" method="post">\n<table class="ctable" cellspacing="0" cellpadding="0">\n<tr style="background:#F6F6F6"><td style="width:10%">商品标题</td><td style="width:90%;text-align:left">\n<input type="text" name="title" class="input" id="title-need" maxlength="200" /> 推荐<input type="checkbox" name="isRec" value="1" />\n</td></tr>\n<tr style="background:#F6F6F6"><td>副标题</td><td style="text-align:left">\n<input type="text" class="input" name="caption" maxlength="200" /></td></tr>\n<tr style="background:#F6F6F6"><td>关键字</td><td style="text-align:left">\n<input type="text" class="input" name="keywords" maxlength="200" /></td></tr>\n<tr style="background:#F6F6F6"><td>商品类型</td><td style="text-align:left">\n<select name="complex" onchange="if(this.value==''1''){$(''#chicuninfo'').show();}else{$(''#chicuninfo'').hide();}">\n<option value="1">普通单品</option>\n<option value="5">固定礼包</option>\n</select>\n 状态\n<select name="jtype" koo="8">\n<option value="1">已下架</option>\n<option value="5">隐藏产品</option>\n<option value="8">已上架</option>\n<option value="3">已售完</option>\n<option value="7">缺货</option>\n</select>\n</td></tr>\n<tr style="background:#F6F6F6" id="chicuninfo"><td>商品属性</td><td style="text-align:left">\n多尺寸<input type="checkbox" name="jsize" value="1" />\n 多色彩<input type="checkbox" name="color" value="1" />\n 参数类型\n<select name="techPara">\n<option value="0">无参数</option>\n<:foreach collection="?params.Keys?" var="pkey">\n<option value="?pkey?">?params[pkey]?</option>\n</:foreach>\n</select>\n\n</td></tr>\n<tr style="background:#F6F6F6"><td>市场价</td><td style="text-align:left"><input type="text" koo="money" class="input" title="市场价不能低于商品价" name="marketPrice" /></td></tr>\n<tr style="background:#F6F6F6"><td>限购数量</td><td style="text-align:left"><input type="text" koo="digit" name="number" class="input" /></td></tr>\n<tr style="background:#F6F6F6"><td>赠送积分</td><td style="text-align:left"><input type="text" koo="digit" value="0" name="score" class="input" /></td></tr>\n<tr style="background:#F6F6F6"><td>商品条码</td><td style="text-align:left"><input type="text" name="barcode" maxlength="100" koo="need" class="input" /></td></tr>\n\n<tr style="background:#F6F6F6"><td>商品型号</td><td style="text-align:left"><input type="text" name="extend" maxlength="100" id="extend" class="input" /></td></tr>\n\n<tr style="background:#F6F6F6"><td>累计销量</td><td style="text-align:left"><input type="text" koo="digit" value="0" name="sales" class="input" /></td></tr>\n<tr style="background:#F6F6F6"><td>上架日期</td><td style="text-align:left"><input type="text" koo="date" name="addtime" class="input" /></td></tr>\n<tr style="background:#F6F6F6"><td>长</td><td style="text-align:left"><input type="text" koo="float" name="lenght" class="input" />cm</td></tr>\n<tr style="background:#F6F6F6"><td>宽</td><td style="text-align:left"><input type="text" koo="float" name="width" class="input" />cm</td></tr>\n<tr style="background:#F6F6F6"><td>高</td><td style="text-align:left"><input type="text" koo="float" name="height" class="input" />cm</td></tr>\n<tr style="background:#F6F6F6"><td>重</td><td style="text-align:left"><input type="text" koo="float" name="weight" class="input" />kg</td></tr>\n</table>\n<div class="actions">\n<ul>\n<li>分类标签</li>\n</ul>\n</div>\n<table cellspacing="0" cellpadding="0">\n<tr style="background:#F6F6F6;">\n<td style="text-align:left">\n<div style="width:325px;float:left">\n<strong>分类</strong><input type="hidden" name="categorys" value="?categoryId?" />\n<ul style="overflow:auto;width:300px;margin-left:20px;">\n?api(''core'').CategoryOption(categoryId,0)?\n</ul>\n</div>\n<div style="width:360px;float:left">\n<strong>标签</strong>\n<ul id="tag78219" style="overflow:auto;width:340px;margin-left:20px;">\n<li id="cate0">\n<:set name="top_tags" value="?api(''core'').TagGroup(categoryId)?" />\n<:foreach collection="?top_tags?" var="ttag">\n<div><input type="checkbox" onclick="sonTag(this)" name="tags" value="?ttag.tagsId?" />?ttag.title?</div>\n</:foreach>\n</li>\n</ul>\n</div>\n</td>\n</tr>\n<tr><td><input type="submit" class="bt" value="提交" /> <input type="reset" class="bt" value="重置" /></td></tr>\n</table>\n</form>\n<script type="text/javascript">\n    function tip() {\n        alert(''请先添加基本信息'');\n    }\n    function setTag(obj) {\n        id = $(obj).val();\n        if ($(obj).attr(''checked'')) {\n            $(''.cls'' + id).show();\n            txt = $(obj).next().text();\n            var tags = ''no'';\n            if ($(''div'').data(id)) {\n                tags = $(''div'').data(id);\n            } else {\n                $.ajax({\n                    url: ''?site("core/category/tag_option")?/'' + id,\n                    type: ''POST'',\n                    async: false,\n                    success: function (msg) {\n                        if (msg == '''')\n                            tags = ''no'';\n                        else\n                            tags = msg;\n                        $("div").data(id, tags);\n                    },\n                    error: function (obj) {\n                        alert(obj.responseText);\n                    }\n                });\n            }\n            if (tags != ''no'') {\n                $(''#tag78219'').append(''<li id="cate'' + id + ''"><div style="margin-left:16px">'' + tags + ''</div></li>'');\n            }\n        }\n        else {\n            del_cat($(obj), 0);\n        }\n    }\n    function sonTag(obj) {\n        var tagId = $(obj).val();\n        if ($(''#t'' + tagId).length > 0) {\n            if ($(''#t'' + tagId).css(''display'') == ''none'') {\n                $(''#t'' + tagId).show();\n            } else {\n                $(''#t'' + tagId).hide();\n                $(''.son'' + tagId).each(function () {\n                    if ($(this).attr(''checked'')) {\n                        $(this).removeAttr(''checked'');\n                    }\n                });\n            }\n        } else {\n            $.ajax({\n                url: ''?site("core/category/tag_sons")?/'' + tagId,\n                type: ''POST'',\n                async: false,\n                success: function (msg) {\n                    $(obj).parent().after(msg);\n                },\n                error: function (obj) {\n                    alert(obj.responseText);\n                }\n            });\n        }\n    }\n    function del_cat(obj) {\n        tid = obj.val();\n        $(''#cate'' + tid).remove();\n        $(''.cls'' + tid + '' input[type="checkbox"]'').each(function () {\n            var fid = $(this).val();\n            var lte = $(''.cls'' + fid);\n            if (lte != null && lte.length > 0) {\n                del_cat($(this));\n            }\n            $(this).attr(''checked'', false);\n            $(this).parent('''').hide();\n            $(''#cate'' + fid).remove();\n        });\n    }\n</script>\n?template("base_foot")?', '1002032132328300875', 1277551000, 1418999381, '1311171004590241754', 5, ''),
