('product_ticket_addmore', '<tr><th>购物券种类</th><td><select name="jstyle" onchange="if(this.value==''3''){\n$(''#latest0114'').hide();}else{$(''#latest0114'').show();}">\n<option value="2">抵值购物券</option>\n<option value="3">折扣购物券</option>\n</select></td></tr>\n<tr><th>购物券号</th><td><input type="text" name="ticket_id" id="id-need" maxlength="10" /></td></tr>\n<tr><th>购物券名称</th><td><input type="text" id="act-jump-need" name="title" maxlength="100" /></td></tr>\n<tr><th>面值金额</th><td><input type="text" id="money-money" name="money" /></td></tr>\n<tr id="latest0114"><th>最低消费</th><td><input type="text" id="latest-money" value="0" name="latest" /></td></tr>\n<tr><th>有效期方式</th><td>\n<select name="jtype" onchange="if(this.value==''2''){$(''#time8212'').show();$(''#day09881'').hide();}else{$(''#time8212'').hide();$(''#day09881'').show();}">\n<option value="2">固定时间段</option>\n<option value="1">发放后计时</option>\n</select>\n</td></tr>\n<tr id="day09881" style="display:none"><th>有效期</th><td>顾客领取&nbsp;<input type="text" style="width:24px;" value="0" id="days-digit" name="start" />&nbsp;天后,&nbsp;<input type="text" value="10" id="days-digit" name="days" style="width:24px;" />&nbsp;天内有效</td></tr>\n<tr id="time8212"><th>有效期</th><td><input type="text" style="width:100px;" id="starttime-date" name="starttime" value="?date(now)?" />&nbsp;至&nbsp;<input type="text" style="width:100px;" id="endtime-date-*starttime$date" name="endtime" value="?date(now)?" /></td></tr>', '1002032132328300875', 1277719810, 1326575740, '1201062002169631163', 5, ''),
