('product_ticket_send', '?header("shop_head")?\n<div id="postion"><a href="?site(''jabinfo012738'')?">桌面</a>发放购物券</div>\n<div class="actions">\n<ul>\n<li>发放购物券</li>\n</ul>\n</div>\n<form action="?site(''product/ticket/send'')?" method="post" id="act-jump">\n<table cellspacing="0" cellpadding="0">\n<tr><td width="50%" style="text-align:right;">用户</td><td style="text-align:left;"><input name="keyword" id="keyword" style="width:150px;" type="text"><a href="#" onclick="up(''?site(''deal/orders/getuser'')?'',''uid'',''keyword=''+$(''#keyword'').val())" class="bt">查询</a></td></tr>\n\n<tr><td style="text-align:right;">选择用户</td><td style="text-align:left;"><select name="uid" id="uid" style="width:150px;"><option value="">请选择用户</option></select></td></tr>\n\n<tr><td style="text-align:right;">购物券</td><td style="text-align:left;">\n<input name="ticket" id="ticket" style="width:150px;" type="text"><a href="#" onclick="up(''?site(''product/ticket/getticket'')?'',''ticket_id'',''keyword=''+$(''#ticket'').val())" class="bt">查询</a></td></tr>\n\n<tr><td style="text-align:right;">选择购物券</td><td style="text-align:left;"><select name="ticket_id" id="ticket_id" style="width:150px;"><option value="">请选择购物券</option></td></tr>\n<tr><td style="text-align:right;"><input type="submit" value="确定"></td><td style="text-align:left;">\n<input type="reset" value="重置"></td></tr>\n\n</table>\n\n</form>\n?template("base_foot")?', '1201062002169631163', 1329509000, 1329510000, '1201062002169631163', 5, ''),
