('pup_article_head', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\r\n<html xmlns="http://www.w3.org/1999/xhtml">\r\n<head>\r\n    <meta http-equiv="Content-Type" content="text/html; charset=utf-8" />\r\n    <title>办公用品网</title>  \r\n    <link href="?site(''res/pup/style.css'')?" type="text/css" rel="Stylesheet" />\r\n    <script type="text/javascript" src="?site(''res/js/lib'')?"></script>\r\n</head>\r\n<body>\r\n    <div id="win">\r\n        <div id="web">\r\n            <div id="container">\r\n                <div id="top">\r\n                    <img src="?skin(''log'')?" alt="" class="log" />\r\n                    <div id="menu">\r\n                        <:set name="var" value="?api(''article'').nav(''0'')?" />\r\n                        <:foreach collection="?var?" var="nv">\r\n                           <a href="?site(nv.Target)?" ?nv.Class? >?nv.Title?</a>\r\n                       </:foreach>\r\n                    </div>\r\n                </div>\r\n                <div id="theme">\r\n                    <img src="?res(''res/pup/img/theme.jpg'')?" alt="" />\r\n                </div>\r\n                <div id="contain">\r\n                    <div id="main">', '1204251659419211169', 1338887300, 1338889090, '1204251659419211169', 7, ''),
