('tma_team_calapp_edit', '<tr><th>名称</th><td>\r\n<input type="text" name="title" id="act-jump-need" value="?calendar.Title?" />\r\n<input type="hidden" name="calendar_id" value="?calendar.Calendar_id?" />\r\n</td></tr>', '1002032132328300875', 1340104060, 1340104060, '1002032132328300875', 8, ''),
('tma_team_calapp_export', '<tr><th>开始时间</th><td><input type="text" name="starttime" id="start-date" value="?start?" /></td></tr>\r\n<tr><th>结束时间</th><td><input type="text" name="endtime" id="end-date" value="?end?" /></td></tr>', '1002032132328300875', 1340104060, 1340104060, '1002032132328300875', 8, ''),
('tma_team_calapp_home', '?template(''tma_team_head'')?\r\n\r\n<div id="main">\r\n\r\n<script type="text/javascript" src="/res/lib/fullcalendar/jquery-ui.js"></script>\r\n<script type="text/javascript" src="/res/lib/fullcalendar/fullcalendar.min.js"></script>\r\n<script type="text/javascript" src="?site(''res/js/cal.js'')?"></script>\r\n\r\n<div id="calendar"></div>\r\n\r\n\r\n    <form action="#" style="display: none" id="calwin" method="post">\r\n        <div class="win_content">\r\n    <h3 class="win_title"><span>新活动</span><a class="calwinclose" href="javascript:void(0)">x</a></h3>\r\n        <table>\r\n            <tfoot><tr><th></th><td><a href="javascript:void(0)" id="jwinsave">保存</a><a href="javascript:void(0)" class="calwinclose">关闭</a>\r\n                <input type="reset" style="display: none" id="jwinreset" />\r\n                <input type="hidden" id="jwinid" />\r\n                <input type="hidden" id="starttime" />\r\n                <input type="hidden" id="endtime" />\r\n                </td></tr></tfoot>\r\n            <tbody class="win_body">\r\n            <tr><th>内容</th><td><input type="text" id="jwintitle" name="jwintitle" /></td></tr>\r\n            <tr><th>颜色</th><td id="tags"><input type="hidden" id="color" value="" /><:foreach collection="?color.Keys?" var="key"><em style="background-color: #?key?" rel="?key?">&nbsp;</em></:foreach></td></tr>\r\n            </tbody>\r\n        </table>\r\n            </div>\r\n    </form>\r\n\r\n\r\n    <div id="jwin"><a id="jwincl" href="javascript:void(0)">x</a><h3></h3><h4></h4><h5>\r\n        <em></em>\r\n        <span><a href="#" class="edit">修改</a><a href="#" class="del">删除</a></span>\r\n    </h5></div>\r\n    \r\n\r\n    <script type="text/javascript">jabinfoCal.Id = '''';jabkey=''-1'';\r\n        $(document).ready(function () {\r\n            $(''#rightbg'').hide();\r\n            calInit();\r\n        });\r\n</script>\r\n</div>\r\n?template("tma_team_foot")?\r\n\r\n', '1002032132328300875', 1340