  cursor: ''pointer'',\r\n                    dataLabels: {\r\n                        enabled: true,\r\n                        color: ''#000000'',\r\n                        connectorColor: ''#000000'',\r\n                        formatter: function() {\r\n                            return ''<b>''+ this.point.name +''</b>: ''+ this.y +'' 个'';\r\n                        }\r\n                    }\r\n                }\r\n            },\r\n            series: [{\r\n                type: ''pie'',\r\n                name: ''Browser share'',\r\n                data: [\r\n                 [''待认领'',?BugNumber(project.Project_id,0)?],\r\n				[''已认领'',   ?BugNumber(project.Project_id,1)?],\r\n				[''已解决'',   ?BugNumber(project.Project_id,5)?],\r\n				[''测试通过'',   ?BugNumber(project.Project_id,15)?],\r\n				[''已延期'',   ?BugNumber(project.Project_id,25)?]\r\n                ]\r\n            }]\r\n        });\r\n    });\r\n</script>\r\n\r\n</div>\r\n\r\n</div>\r\n\r\n</div>\r\n?template("tma_team_foot")?', '1002032132328300875', 1340104060, 1340104060, '1002032132328300875', 8, ''),
