('tma_team_setting_stat', '?template("tma_team_head")?\r\n<div id="main">\r\n    <div class="stattitle">\r\n        <script type="text/javascript" src="?site(''res/lib/chart/highcharts.js'')?"></script>\r\n        <h5><span>?name?:?start?至?end?日程统计</span>\r\n            <div>\r\n                <a href="#" class="redbt" onclick="mywin(''/team/setting/stat/?id?'', ''/team/setting/stat'', ''日程统计'')">\r\n                    日程统计</a>\r\n            </div>\r\n        </h5>\r\n        <p id="task_chart" style="width: 99%; height: 420px"></p>\r\n        <script type="text/javascript">\r\n            $(''#rightbg'').remove();\r\n            var showYear=null;\r\n            var chart={\r\n                chart: {\r\n                    renderTo: ''task_chart'',\r\n                    type: ''area'',\r\n                    marginRight: 5,\r\n                    marginBottom: 30\r\n                },\r\n                title: {\r\n                    text: null,\r\n                },\r\n                xAxis: {\r\n                    categories: [?categorys?]\r\n                },\r\n                yAxis: {\r\n                    title: {\r\n                        text: ''''\r\n                    },\r\n                    plotLines: [{ value: 0, width: 1, color: ''#008cd8'' }]\r\n                },\r\n                tooltip: {\r\n                    formatter: function () {\r\n                        return ''<b>'' +showYear+ this.x + ''</b><br/>'' + ''完成 '' + this.y + ''个任务'';\r\n                    }\r\n                },\r\n                legend: {\r\n                    enabled: false\r\n                },\r\n                series: [{\r\n                    name: null,\r\n                    color: "#498BF5",\r\n                    fillOpacity: .1,\r\n                    data: [?data?]\r\n                }],\r\n                credits: {\r\n                    enabled: false\r\n                }\r\n            };\r\n            if(chart.xAxis.categories==undefined||