('tma_team_taskapp_add', '<tr><th>所属项目</th><td><select name="project_id" id="project_id-need"><option value="">选择项目</option>\r\n<:foreach collection="?projectList?" var="project">\r\n<option value="?project.Project_id?">?project.Title?</option>\r\n</:foreach></select><a href="?site(''team/projectapp/home'')?">添加项目</a>\r\n</td></tr>\r\n<tr><th>任务名称</th><td><textarea name="title" id="title-need" cols="3" rows="3"></textarea></td></tr>\r\n<tr><th>等级</th><td class="dataselect">\r\n<input type="hidden" class="dataval" id="act-file" name="grade" value="1" />\r\n<em class="taskcur" style="background-color: #D74937;" data="1">普通</em><em data="3">紧急</em><em data="0">延期</em></td></tr>\r\n<tr><th>相关人员</th>\r\n <td id="person">\r\n<input type="text" class="auto" title="开发人" value="开发人" /><input type="hidden" name="developers" />\r\n<input type="text" class="auto" title="测试人" value="测试人" /><input type="hidden" name="testers" />\r\n<input type="text" class="auto" title="上报人" disabled="disabled" id="post_uid" value="上报人" /><input type="hidden" name="post_uid" /></td></tr>\r\n<tr><th>预计时间</th><td class="dataselect">\r\n    <input name="starttime" type="text" value="?now?" id="start-date" style="width:116px;">\r\n    <input type="hidden" class="dataval" name="timetype" value="1" />\r\n    <em style="background-color: #D74937;" class="taskcur" data="1">上午</em><em data="2">下午</em><em data="4">全天</em></td></tr>\r\n<tr><th>附件1</th><td><input name="file1" class="mytip" title="附件不能超过1M" type="file" id="file1"></td></tr>\r\n<tr><th>附件2</th><td><input name="file2" class="mytip" title="附件不能超过1M" type="file" id="file2">\r\n<script type="text/javascript">\r\n    $(''.auto'').blur(function () { if ($(this).val() == '''') { $(this).val($(this).attr(''title'')); } });\r\n    $(''.auto'').focus(function () { if ($(this).val() == $(this).attr(''title'')) { $(this).val(''''); } });\r\n    $(''.auto'').typeahead({source: [?users?]});\r\n    $(''.dataselect em'').click(function(){\r\n        var paren