('tm_team_member_index', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\n<html xmlns="http://www.w3.org/1999/xhtml">\n<head>\n    <meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />\n    <title>Rong Mind-Thanks RongFang,My Lover</title>\n    <link rel="Stylesheet" type="text/css" href="?res(''resource/tm/css.css'')?" />\n<script type="text/javascript" src="?file(''resource/js/res_lib.js'')?"></script>\n</head>\n<body class="bg2">\n<div id="header" class="bg">\n<div id="logo">\n<img src="?res(''resource/tm/img/logo_min.png'')?" alt="Rong Mind" />\n<input id="keyword" type="text" value="Press Enter key search your mindmap" />\n<div id="action">\n<div>Hi,?nick(uid)?!</div>\n<a href="#" onclick="mywin(''?site(''team/member/setting'')?'',''?site(''team/member/setting'')?'',''Setting Profile'')" title="Setting" class="setting"></a>\n<a href="#" onclick="mywin(''?site(''team/member/feedback'')?'',''?site(''team/member/feedback'')?'',''Feedback'')" title="Feedback" class="feedback"></a>\n<a href="?site(''team/home/logout'')?" title="Logout" class="logout"></a>\n</div>\n</div>\n\n</div>\n\n<div id="main">\n<div id="tool">\n<div id="new"><a href="?site(''team/member/create'')?" class="new"><div></div>New</a></div>\n<div id="page">?pager(''team/member/index/{0}'',index,total,size)?</div>\n</div>\n\n<br clear="all" />\n<div id="content">\n<:foreach collection="?mindlist?" var="mind">\n<dl>\n<dt>\n<a href="?site(''team/member/edit'')?/?mind.Mind_id?" class="edit" title="Edit"></a>\n<a href="?site(''team/member/locked'')?/?mind.Mind_id?"\n<:if test="?mind.Privacy is ''0''?"> class="rlock" title="Unlock"<:else> class="lock" title="Locked"</:if>></a>\n<a href="#" onclick="del(''?site(''team/member/delete'')?/?mind.Mind_id?'')" class="del" title="Remove"></a>\n</dt>\n<dd>\n\n<h1>?mind.Title?</h1>\n<h6>?mind.Describe?</h6>\n<div class="author">\nAt ?idate(mind.Mind_id)?\n</div>\n\n<:if test=