document.domain + $(target).parent().first().text();\n        window.open(url + "?keyword()?status=" + encodeURIComponent(content), "_blank");\n    };\n    fav.url = function (id) {\n        $(''#sinbo7893t'').remove();\n        content = "http://" + document.domain + ''/share/'' + id;\n        $(''body'').append(''<form style="top: 130px; left: 207px;" id="sinbo7893t" method="post" action="#"><div id="win_content"><h3 id="win_title">Share MindMap<a onclick="$(\\''#sinbo7893t\\'').remove();" href="#">x</a></h3><table><tbody id="win_body"><tr><td colspan="2" style="text-align:center"><input style="width:320px;margin-top:10px" type="text" value="'' + content + ''"></td></tr><tr><th>Tip</th><td>Does not support IE6, IE7, IE8 browser</td></tr></tbody></table></div></form>'');\n        $(''#sinbo7893t'').show();\n    }\n</script>\n<script type="text/javascript" src="?file(''resource/js/res_jabinfo.js'')?"></script>\n</body>\n</html>', '1002032132328300875', 1325949820, 1326542460, '1002032132328300875', 8, ''),
