('zento_document_md', '<!DOCTYPE html>\n<html>\n<head>\n    <meta charset="utf-8"/>\n    <title>Zento Editor</title>\n    <link rel="stylesheet" type="text/css" href="http://b.zento.me/base/css/todo.css"/>\n    <!--<link rel="stylesheet" type="text/css" href="http://b.zento.me/base/css/md.css"/>-->\n    <script src="http://b.zento.me/res/js/lib.min.js"></script>\n    <script src="http://b.zento.me/res/ace/src-min/ace.js"></script>\n    \n</head>\n</html>\n<body>\n<div id="J_nav" class="zen-nav padding">\n    <ul class="rain-left">\n        <li class="save">保存</li>\n        <li class="preview">预览</li>\n    </ul>\n    <div id="J_tip"></div>\n</div>\n<pre id="J_code">?document.content?</pre>\n<div id="J_preview" data-id="?document.documentId?">\n    <span class="rain-icon close">X</span>\n    <div class="md-preview"></div>\n</div>\n<script src="http://b.zento.me/res/markdown/Markdown.Converter.js"></script>\n<script src="http://b.zento.me/base/js/page/markdown.js"></script>\n</body></html>', '1002032132328300875', 0, 1423580533, '1002032132328300875', 5, ''),
