('zento_document_ppt', '<!DOCTYPE html>\n<html>\n<head>\n    <meta charset="utf-8"/>\n    <title>PPT</title>\n    <link rel="stylesheet" type="text/css" href="http://b.zento.me/base/css/md.css"/>\n    <script src="http://b.zento.me/res/remark-0.5.9.js"></script>\n</head>\n<body>\n<textarea id="sourceHandle" style="display:none">?content?</textarea>\n<script>\n    var content = document.getElementById("sourceHandle").value;\n    if(!content && !!window.parent){\n        content = window.parent.md.getValue();\n    }\n    remark.create({source:content});\n</script>\n</body></html>', '1002032132328300875', 0, 1420921101, '1002032132328300875', 5, ''),
