('deal_orders_back', '?header("shop_head")?\n<div id="postion"><a href="?site(''jabinfo012738'')?">桌面</a>发货单列表</div>\n<div class="actions">\n<ul>\n<li><a href="#" onclick="mywin(''?site(''deal/orders/search_option'')?'',''?site(''deal/orders/search'')?'',''订单搜索'')">订单搜索</a></li>\n</ul>\n</div>\n<table cellspacing="0" cellpadding="0">\n <tfoot><tr><th colspan="9"><script type="text/javascript">pager(''?site("deal/orders/back/{0}")?'',?index?,?total?,?size?);</script></th></tr></tfoot>\n<tbody>\n<tr><th style="width:15px"></th><th width="18%">会员名</th><th width="11%">收货人</th><th width="10%">手机号</th><th width="10%">订单金额</th><th width="15%">下单时间</th><th width="10%">支付方式</th><th width="10%">状态</th><th width="18%">操作</th>\n</tr>\n<:foreach collection="?ordersList?" var="orders" forindex="?start?">\n<tr>\n<th>?forindex?</th>\n<td><a href="?site(''deal/orders/edit'')?/?orders.Orders_id?">?username(orders.Uid)?</a></td>\n<td>?orders.Reciever?</td>\n<td>?orders.Mobile?</td>\n<td>?orders.Money?</td>\n<td>?date(orders.Addtime)?</td>\n<td>?paytype[orders.Pay_type]?</td>\n<td>?jstatus[orders.Jstatus]?</td>\n<td><a href="#" onclick="mywin(''?site(''deal/orders/edit/?orders.Orders_id?'')?'',''?site(''deal/orders/edit'')?'',''编辑订单'')">强制修改</a>\n | <a href="#" onclick="del(''?site(''deal/orders/remove/?orders.Orders_id?'')?'')">删除</a></td></tr>\n</:foreach>\n</tbody>\n</table>\n?template("base_foot")?', '1201062002169631163', 1329482000, 1329482370, '1201062002169631163', 5, ''),
