('deal_orders_edit', '?header("shop_head")?\r\n<div id="postion"><a href="?site(''jabinfo012738'')?">桌面</a><a href="?site(''deal/orders/home'')?">订单列表</a>查看订单</div>\r\n<div class="actions">\r\n<ul>\r\n<li>订单信息</li>\r\n<li><a href="?site(''deal/orders/log'')?/?orders.Orders_id?">订单日志</a></li>\r\n</ul>\r\n</div>\r\n<table cellspacing="0" cellpadding="0">\r\n<tr><td width="15%" style="text-align:right;padding-right:8px;">订单编号</td><td width="35%" style="text-align:left">?orders.Orders_id?</td><td width="15%" style="text-align:right;padding-right:8px;">订单状态</td><td width="35%" style="text-align:left">?jstatus[orders.Jstatus]?</td></tr>\r\n\r\n<tr><td style="text-align:right;padding-right:8px;">会员名</td><td style="text-align:left">?username(orders.Uid)?&nbsp;&nbsp;<a href="?site(''deal/orders/info/?orders.Uid?'')?" >基本资料</a>&nbsp;&nbsp;<a href="?site(''deal/orders/history/?orders.Uid?'')?">历史订单</a></td><td style="text-align:right;padding-right:8px;">下单时间</td><td style="text-align:left">?date(orders.Addtime)?</td></tr>\r\n\r\n<tr><td style="text-align:right;padding-right:8px;">收货人</td><td style="text-align:left">?orders.Reciever?\r\n<:if test="?isEdit?">\r\n&nbsp;&nbsp;<a href="#" onclick="mywin(''?site(''deal/orders/address'')?'',''?site(''deal/orders/address'')?'',''用户基本资料'')">修改收货信息</a>\r\n</:if>\r\n</td><td style="text-align:right;padding-right:8px;">联系电话</td><td style="text-align:left">?orders.Mobile?&nbsp;&nbsp;&nbsp;?orders.Telphone?</td></tr>\r\n\r\n<tr><td style="text-align:right;padding-right:8px;">收货地址</td><td colspan="3" style="text-align:left">?pid?&nbsp;?cid?&nbsp;?sid?&nbsp;?orders.Address?&nbsp;&nbsp;邮编:?orders.Postcode?</td></tr>\r\n\r\n<tr><td style="text-align:right;padding-right:8px;">支付方式</td><td style="text-align:left">?paytype[orders.Pay_type]?</td><td style="text-align:right;padding-right:8px;">送货时间</td><td style="text-align:left">?time[orders.Sendtime]?</td></tr>\r\n\r\n<tr><td style="text-align:right;padding-right:8px;">物流公司</td><td colspan="3" style="text-align:left">?deliver?&nbsp;&nbsp;\r\n<:if test="?orders.Deliver_id not ''''?">\r\n发货时间：?date(orders.Deliver_time)?&nbsp;&nbsp;快递号：?orders.Deliver_id?<:else>(<span style="color:red">未发货</span>)</:if>\r\n</td></tr>\r\n</table>\r\n\r\n<div class="actions">\r\n<ul>\r\n<li>商品信息</li>\r\n<:if test="?isEdit?"><li><a href="#" onclick="mywin(''?site(''deal/shop/add'')?/?orders_id?'',''?site(''deal/shop/add'')?'',''添加商品'')">添加商品</a></li></:if>\r\n</ul>\r\n</div>\r\n<table cellspacing="0" cellpadding="0">\r\n<tr><th width="55%">产品名称</th><th width="10%">单价</th><th width="10%">数量</th><th width="10%">小计</th><th width="15%">操作</th></tr>\r\n<:foreach collection="?shops?" var="shop">\r\n<tr><td style="text-align:left">?shop.Title?</td><td>?shop.Money?元</td><td>?shop.Number?</td><td>?shop.Total?元</td><td><a href="?site(''product/home/detail'')?/?shop.Product_id?" target="_blank">查看</a>\r\n<:if test="?isEdit?">\r\n&nbsp;|&nbsp;<a href="#" onclick="mywin(''?site(''deal/shop/edit'')?/?shop.Object_id?'',''?site(''deal/shop/edit'')?'',''添加订单'')">编辑</a>\r\n&nbsp;|&nbsp;<a href="#" onclick="del(''?site(''deal/shop/remove'')?/?shop.Object_id?'')">删除</a>\r\n</:if>\r\n</td></tr>\r\n</:foreach>\r\n\r\n<tr style="background:#F6F6F6;height:45px;font-size:14;font-weight:bold;"><td colspan="5" style="text-align:left">商品金额：?orders.Money?元 + 运费：?orders.Psmoney?元 - 余额：?orders.Balance?元 - 购物券：?orders.Ticket_money?元 - 积分抵值：?orders.Score_money?元 - 返现：?orders.Rabate?元&nbsp;&nbsp;应付总额：<span style="color:red;font-weight:bold">￥?money(orders.Pay)?</span>元</td></tr>\r\n<tr style="background:#F6F6F6"><td colspan="5">?jstatus[orders.Jstatus]?&nbsp;&nbsp;?action?<a href="#" class="bt" onclick="mywin(''?site(''deal/orders/bak'')?/?orders_id?'',''?site(''deal/orders/bak'')?'',''添加备注'')">备注</a></td></tr>\r\n</table>\r\n\r\n<div class="actions">\r\n<ul>\r\n<li>订单备注</li>\r\n</ul>\r\n</div>\r\n<table cellspacing="0" cellpadding="0">\r\n<tr><th width="15%">备注人</th><th width="65%">备注内容</th><th width="20%">日期</th