('article_answers_home', '?header("base_head")?\n<div id="postion"><a href="?site(''jabinfo012738'')?">桌面</a><a href="?site(''article/paper/home'')?">调查文件列表</a>\n<a href="?site(''article/question/home'')?">问题列表</a>答卷列表</div>\n<div class="actions">\n<ul>\n<li><a href="#" onclick="mywin(''?site(''article/answers/add'')?'',''?site(''article/answers/add'')?'',''添加答卷'')">添加答卷</a></li>\n</ul>\n</div>\n<table cellspacing="0" cellpadding="0">\n <tfoot><tr><th colspan="3"><script type="text/javascript">pager(''?site("article/answers/home/{0}")?'',?index?,?total?,?size?);</script></th></tr></tfoot>\n<tbody>\n<tr><th width="20%">选项内容(逗号分隔)</th><th width="20%">回答内容</th><th width="15%">操作</th>\n</tr>\n<:foreach collection="?answersList?" var="answers">\n<tr>\n<td>?answers.Options?</td>\n<td>?answers.Content?</td>\n<td><a href="#" onclick="mywin(''?site(''article/answers/edit/?answers.Answers_id?'')?'',''?site(''article/answers/edit'')?'',''编辑答卷'')">编辑</a>\n | <a href="#" onclick="del(''?site(''article/answers/remove/?answers.Answers_id?'')?'')">删除</a></td></tr>\n</:foreach>\n</tbody>\n</table>\n?template("base_foot")?', '1006200948563828525', 1277198720, 1277222660, '1006200948563828525', 5, ''),
