('base_authority_resource', '?template(''base_head'')?\n<div class="position"><a href="/jabinfo012738">桌面</a>资源列表</div>\n<div class="actions"><ul>\n<li><a href="/base/authority/add_controller" onclick="mywin()">添加controller</a></li>\n</ul>\n</div>\n<script type="text/javascript">\nfunction showRes(obj,cls){	\nif($(obj).attr(''class'')==''res''){\n		$(''.''+cls).show();		\n		$(obj).attr(''class'',''ts'');	\n}else{\n		$(''.''+cls).hide();\n		$(obj).attr(''class'',''res'');\n}}\n</script>\n<table cellspacing="0" cellpadding="0">\n<tr><th width="35%">控制器</th><th width="35%">行为</th><th  width="30%">操作</th></tr>\n<:foreach collection="?resource?" var="res">\n<tr><td onclick="showRes(this,''?res.Module?_?res.Controller?'')" class="res">?res.Module?.?res.Controller?-?res.Title?</td>\n<td></td>\n<td><a href="/base/authority/add_action/?res.Module?/?res.Controller?" onclick="mywin()">添加动作</a>\n<a href="/base/authority/edit_controller/?res.Module?/?res.Controller?" onclick="mywin(''编辑?res.Title?'')">编辑</a>\n<a href="/base/authority/move/?res.Module?/?res.Controller?" onclick="del()">上移</a>\n<a href="/base/authority/move/?res.Module?/?res.Controller?/1" onclick="del()">下移</a>\n<a href="/base/authority/delete_resource/?res.Module?/?res.Controller?" onclick="del(this)">删除</a></td>\n</tr>\n<:foreach collection="?res.Actions?" var="action">\n<tr style="display:none" class="?res.Module?_?res.Controller?"><td></td><td>?action.Action?-?action.Title?</td>\n<td><a href="/base/authority/edit_action/?res.Module?/?res.Controller?/?action.Action?" onclick="mywin(''编辑?action.Title?'')">编辑</a>\n<a href="/base/authority/delete_resource/?res.Module?/?res.Controller?/?action.Action?" onclick="del(this)">删除</a></td></tr></:foreach>\n</:foreach>\n</table>?template(''base_foot'')?', '1002032132328300875', 1265721437, 1412867548, '1002032132328300875', 4, ''),
('base_authority_role', '?header(''base_head'')?\n<div class="position">?role?权限</div>\n<div class="actions"><ul>\n<li><a href="/base/authority/user_add/?role_id?" onclick="mywin(''/base/authority/user_add'')">添加用户</a></li>\n</ul></div>\n<table cellspacing="0" cellpadding="0">\n<tr><th width="15%">用户名</th><th width="85%">操作</th></tr>\n<:foreach collection="?users?" var="user"><tr><td><a href="/base/authority/personal/?user.Uid?">?user.Username?</a></td>\n<td><a href="/base/authority/user_delete/?user.Uid?" onclick="del()">删除</a></td></tr>\n</:foreach>\n</table>\n\n<div class="position">角色权限</div>\n<div class="actions"><ul>\n<li><a href="#" onclick="up(''/base/authority/update_roles/?role_id?'',''prompt'')">更新权限缓存</a></li>\n</ul></div>\n<table cellspacing="0" cellpadding="0">\n<tr><th width="15%">功能模块</th><th width="85%">权限</th></tr>\n?content?\n</table>\n\n<script type="text/javascript">\nvar flag=true;\nfunction setright(obj,key,content)\n{\n	if(flag==false){return;}\n	flag=false;\n	var img=$(obj).children();\n	url=$(img).attr("src");\n	if(url.indexOf(''no'')<0)\n	$(img).attr("src","/res/img/no");\n	else\n	$(img).attr("src","/res/img/yes");\n	$.ajax({\n	async:false,\n	type:''POST'',\n	url:''/base/authority/power'',\n	data:''uid=role?role_id?&key=''+key+''&content=''+content,\n	success:function(html){flag=true;content='''';key=''''}\n	});\n}\n</script>\n?template(''base_foot'')?', '1002032132328300875', 1265738867, 1418992469, '1002032132328300875', 4, ''),
