('base_coder_detail', '<!DOCTYPE html>\n<html>\n<head>\n    <meta http-equiv="Content-Type" content="text/html; charset=utf-8"/>\n    <title>?name? Database</title>\n    <meta name="google" value="notranslate">\n    <link type="text/css" href="http://b.zento.me/base/css/dbmap.css" rel="Stylesheet"/>\n    <script src="/res/js/lib"></script>\n    <script src="http://b.zento.me/res/jquery-ui-1.10.4.custom.min.js"></script>\n</head>\n<body>\n<ul id="nav">\n    <li class="new">新建</li>\n    <li class="save">保存</li>\n    <li class="coder">模版</li>\n    <li class="sql">SQL</li>\n    <!--<li class="func">Func</li>-->\n    <!--<li class="undo">撤消</li>-->\n    <!--<li class="query">查询</li>-->\n    <!--<li class="dict"><a href="/base/coder/dict/?dbId?" target="_blank"></a>字典</a></li>-->\n</ul>\n<div id="J_tip"></div>\n<div id="container">\n    <div id="content"></div>\n</div>\n<div id="J_dialog">\n    <h2>\n        <span class="title"></span><em class="close">x</em>\n    </h2>\n    <div class="content"></div>\n    <h3><span class="save">保存</span><span class="close">取消</span></h3>\n</div>\n<div id="J_coder">\n    <span class="close" onclick="$(''#J_coder'').toggle()">X</span>\n    <div class="col1" id="J_list"></div>\n    <div class="col2">\n        <div class="arrow" onclick="$(''#J_coder'').toggleClass(''code'')">«</div>\n        <iframe id="J_frame" name="editor" src="/base/template/edit"></iframe>\n    </div>\n</div>\n<div id="J_query">\n    <!--<iframe id="J_queryFrame" src="/base/coder/query/?dbId?"></iframe>-->\n</div>\n<script type="text/javascript">\n    var dbmap = {}, scope = {}, database = ?content?;\n</script>\n<script type="text/javascript" src="http://b.zento.me/base/js/page/dbmap.js"></script>\n</body>\n</html>', '1', 1, 1416569366, '1002032132328300875', 5, 'base'),
('base_coder_home', '?template(''base_head'')?\n<div class="position"><a href="/jabinfo012738">桌面</a>数据库连接</div>\n<div class="actions">\n    <ul><li><a href="/base/coder/dbnew" onclick="mywin()">添加连接</a></li></ul>\n</div>\n<table cellspacing="0" cellpadding="0">\n<tr><th width="75%">连接名称</th><th width="25%">操作</th></tr>\n<:foreach collection="?connects.Keys?" var="key">\n        <tr><td><a href="/base/coder/detail/?key?" target="_blank">?key?</a></td>\n        <td><a href="/base/coder/dbremove/?key?" onclick="del(''tr'')">删除</a></td>\n        </tr>\n</:foreach>\n</table>\n?template(''base_foot'')?', '1002032132328300875', 1265232953, 1418142367, '1311171004590241754', 5, ''),
('base_coder_query', '<!DOCTYPE html>\n<html>\n<head>\n    <meta charset="UTF-8">\n    <title>Jabinfo SQL Editor</title>\n    <link type="text/css" href="http://dev/taobao/rainbow/res/dev/debug/css/ace.css" rel="Stylesheet"/>\n    <script src="http://test.taobao.net/rainbow/rainbow/debug/js/lib/jquery-1.10.2.min.js"></script>\n    <script src="http://dev/taobao/rainbow/res/dev/debug/js/mods/dump.js"></script>\n</head>\n<body>\n<div id="J_tip"></div>\n<pre id="editor">?content?</pre>\n<span class="button">查询</span>\n<div id="J_result"></div>\n<script src="http://dev/res/ace/src-min/ace.js"></script>\n<script src="http://dev/res/ace/src-min/ext-language_tools.js"></script>\n<script>\n    ace.require("ace/ext/language_tools");\n    var editor = ace.edit("editor");\n    editor.session.setMode("ace/mode/sql");\n    editor.setTheme("ace/theme/xcode");\n    editor.setFontSize("14px");\n    editor.setShowPrintMargin(false);\n    editor.setHighlightActiveLine(false);\n    editor.setBehavioursEnabled(true);\n    editor.setOptions({\n        enableBasicAutocompletion: true,\n        enableSnippets: true,\n        highlightActive:false,\n        showGutter:false,\n        enableLiveAutocompletion: false\n    });\n    var dbId = ''?dbId?'';\n    editor.commands.addCommands([\n        {\n            name: "showSettingsMenu",\n            bindKey: {win: "Ctrl-s", mac: "Command-s"},\n            exec: query,\n            readOnly: true\n        }\n    ]);\n</script>\n</body>\n</html>\n\n', '1002032132328300875', 0, 1408467844, '', 5, ''),
