INSERT INTO `templates` (`template_id`, `content`, `uid`, `addtime`, `update_time`, `update_uid`, `stype`, `module`) VALUES
('base_template_sql', '<!DOCTYPE html>\n<html>\n<head>\n    <meta charset="UTF-8">\n    <title>Jabinfo Editor</title>\n    <link type="text/css" href="http://b.zento.me/base/css/dbmap.css" rel="Stylesheet"/>\n    <script src="/res/js/lib"></script>\n    <script src="http://b.zento.me/res/ace/src-min/ace.js"></script>\n    <script src="http://b.zento.me/res/ace/src-min/ext-language_tools.js"></script>\n    <script>dbmap={};dbmap.iframe=true;</script>\n    <script src="http://b.zento.me/res/ace/src-min/ace.js"></script>\n    <script src="http://b.zento.me/res/ace/src-min/ext-language_tools.js"></script>\n    <style>\n        div,iframe,textarea{ime-mode:disabled}\n    </style>\n</head>\n<body id="iframe">\n    <form id="J_form">\n        <dl class="left">\n            <dt>方法类型</dt>\n            <dd>\n                <select name="resultType" id="J_type" koo="?sqlVO.resultType?">\n                    <option value="">请选择</option>\n                    <option value="select">Select</option>\n                    <option value="update">Update</option>\n                    <option value="insert">Insert</option>\n                    <option value="count">Count</option>\n                    <option value="delete">Delete</option>\n                    <option value="sum">Sum</option>\n                    <option value="ids">Ids</option>\n                    <option value="json">Json</option>\n                    <option value="row">Row</option>\n                    <option value="one">One</option>\n                </select>\n                <input type="hidden" id="J_sqlVal" name="sql" value="" />\n                <input type="hidden" name="dbname" value="?sqlVO.dbname?" />\n                <input type="hidden" name="tablename" value="?sqlVO.tablename?" />\n                <input type="hidden" id="sqlId" name="sqlId" value="?sqlVO.sqlId?">\n                &nbsp;<a href="/base/template/sql/?sqlVO.dbname?/?sqlVO.tablename?" class="btn">添加方法</a>\n            </dd>\n        </dl>\n        <dl class="left">\n            <dt>方法名称</dt>\n            <dd><input type="text" name="name" value="?sqlVO.name?">\n        </dl>\n        <dl class="left">\n            <dt>方法描述</dt>\n            <dd><input type="text" name="describe" value="?sqlVO.describe?" /></dd>\n        </dl>\n        <dl class="left" id="J_page" style="display:none">\n            <dt>开启分页</dt>\n            <dd><input type="checkbox" name="size" koo="?sqlVO.size?" value="1" /></dd>\n        </dl>\n        <dl id="J_field" class="left" value="?sqlVO.fields?">\n            <dt>字段列表</dt>\n            <dd style="width: 400px;">\n                <:foreach collection="?table.columns?" var="column">\n                <input type="checkbox" id="m_?column.name?" name="fields" value="?column.name?"><label for="m_?column.name?">?column.name?</label>\n                </:foreach>\n                <span id="J_insertField" style="display:none" class="btn">插入</span>\n            </dd>\n        </dl>\n        <dl class="left" id="J_text" style="display:none">\n            <dt>SQL语句</dt>\n            <dd><pre id="J_sql">?sqlVO.sql?</pre></dd>\n        </dl>\n        <dl class="left">\n            <dt>&nbsp;</dt>\n            <dd><span id="J_button">保存</span></dd>\n        </dl>\n    </form>\n    <div id="J_tip"></div>\n    <script src="http://b.zento.me/base/js/page/dbmap.js"></script>\n    <script>dbmap.sqlEditor("?sqlVO.tablename?","?sqlVO.resultType?")</script>\n</body>\n</html>\n\n', '1002032132328300875', 0, 1417883693, '1002032132328300875', 5, ''),
('base_template_sqlList', '<div class="search">\n    <span class="btn" onclick="dbmap.design(''?table?'')">模版设计</span>\n    <span class="btn" onclick="dbmap.func(''?table?'')">添加函数</span>\n    <!--<a href="/base/template/sql/?dbname?/?table?" target="editor" class="btn">添加函数</a>-->\n</div>\n<table>\n    <thead>\n        <tr>\n            <th>名称</th>\n            <th>备注</th>\n            <th>删除</th>\n        </tr>\n    </thead>\n    <tbody>\n    <:foreach collection="?funcs?" var="table">\n    <tr>\n        <td><a href="/base/template/sql/?table.sqlId?" target="editor">?table.name?-<em>?table.resultType?</em></a></td>\n        <td>?table.describe?</td>\n        <td><a href="/base/template/sqlDelete/?table.sqlId?" class="del">删除</a></td>\n    </tr>\n    </:foreach>\n    </tbody>\n</table>', '1002032132328300875', 0, 1416569973, '1002032132328300875', 5, ''),
('base_user_add', '<tr><th>用户名</th><td><input type="text" id="act-jump-need" name="username" /></td></tr>\n<tr><th>用户密码</th><td><input type="password" id="pwd" name="pwd" /></td></tr>\n<tr><th>确认密码</th><td><input type="password" id="pwd2-#pwd-need" name="pwd2" /></td></tr>\n<tr><th>昵称</th><td><input type="text" id="nick-need" name="nick" /></td></tr>', '1002032132328300875', 1265193980, 1267312175, '1002032132328300875', 4, ''),
('base_user_cache', '<tr><th>选择模块</th><td>\r\n<input type="checkbox" style="width:13px;" value="home,index001" name="path" />首页&nbsp;&nbsp;\r\n<:foreach collection="?cache.Keys?" var="key">\r\n<input type="checkbox" style="width:13px;" value="?key?" name="path" />?cache[key]?&nbsp;&nbsp;\r\n</:foreach>\r\n<input type="checkbox" style="width:13px;" value="all" name="path" />全部缓存</td></tr>\r\n<tr><td class="red" style="text-align:center" colspan="2">更新缓存后网站速度会变慢，5分钟后恢复正常，请勿频繁更新</td></tr>', '1002032132328300875', 1281797380, 1330082300, '1002032132328300875', 4, ''),
