('base_user_search', '?template("base_head")?\n<div id="postion"><a href="?site(''jabinfo012738'')?">桌面</a>用户列表</div>\n<div class="actions"><ul>\n<li><a href="/base/user/add/0" onclick="mywin()">添加用户</a></li>\n<li><a href="/base/user/search_view" onclick="mywin(''会员搜索'',''/base/user/search'')">会员搜索</a></li>\n</ul>\n</div>\n<table cellspacing="0" cellpadding="0">\n<tfoot><tr><th colspan="7"><script type="text/javascript">pager(''/base/user/search/{0}'',?index?,?total?,?size?);</script></th></tr></tfoot>\n<tbody>\n<tr><th width="15%">用户名</th><th width="15%">昵称</th>\n<th width="18%">邮箱</th>\n<th width="8%">登录次数</th>\n<th width="18%">最后登录</th>\n<th width="10%">类型</th><th width="15%">操作</th>\n</tr>\n<:foreach collection="?users?" var="u">\n<td>?u.Username?</td>\n<td>?u.Nick?</td>\n<td>?u.Email?</td>\n<td>?u.Logins?</td>\n<td>?date(u.Lasttime)?</td>\n<td>?type[u.Rate]?</td>\n<td>\n<a href="/base/user/password/?u.Uid?" onclick="mywin(''重置?u.Username?的密码'')">重置密码</a> | \n<a href="/base/user/remove/?u.Uid?" onclick="del()'')">删除</a></td></tr>\n</:foreach>\n</tbody>\n</table>\n?template("base_foot")?', '1002032132328303474', 1276859390, 1412866118, '1002032132328300875', 4, ''),
