('base_user_task_add', '<tr><th>名称</th><td><input id="act-jump-char" type="text" value="" name="name" /></td></tr>\n<tr id="last_more"><th>类型</th><td>\n<select name="type" onchange="set_tk(this)">\n<option value="span">循环</option>\n<option value="day">天</option>\n<option value="week">星期</option>\n<option value="month">月</option>\n</select>\n</td></tr>\n<tr class="tkcnt"><th>间隔时间</th><td><input type="text" name="blunder" id="blunder-digit" /><span>(至少大于180)</span></td></tr>\n<tr><th>任务描述</th><td><textarea name="describe" id="describe-need" rows="6" cols="8"></textarea></td></tr>\n', '1106150918461672278', 1313518000, 1313574530, '1106150918461672278', 4, ''),
('basic_business_home', '?header("core_head")?\n<div id="postion"><a href="?site(''jabinfo012738'')?">桌面</a>用户列表</div>\n<div class="actions">\n<ul>\n<li><a href="#" onclick="mywin(''?site(''basic/business/add'')?'',''?site(''basic/business/add'')?'',''添加商业用户'')">用户搜索</a></li>\n</ul>\n</div>\n<table cellspacing="0" cellpadding="0">\n <tfoot><tr><th colspan="4"><script type="text/javascript">pager(''?site("basic/business/home/{0}")?'',?index?,?total?,?size?);</script></th></tr></tfoot>\n<tbody>\n<tr><th width="30%">用户编号</th><th width="20%">用户名</th><th width="20%">真实姓名</th><th width="30%">操作</th></tr>\n<:foreach collection="?businessList?" var="business">\n<tr>\n<td>?business.Uid?</td>\n<td>?username(business.Uid)?</td>\n<td>?business.Realname?</td>\n<td><a href="#" onclick="mywin(''?site(''basic/business/edit/?business.Uid?'')?'',''?site(''basic/business/edit'')?'',''编辑商业用户'')">编辑</a>\n | <a href="#" onclick="del(''?site(''basic/business/remove/?business.Uid?'')?'')">删除</a></td></tr>\n</:foreach>\n</tbody>\n</table>\n?template("base_foot")?', '1002032132328300875', 1323190140, 1323190910, '1002032132328300875', 5, ''),
('basic_feedback_home', '?header("core_head")?\r\n<div id="postion"><a href="?site(''jabinfo012738'')?">桌面</a>用户反馈</div>\r\n<div class="actions">\r\n<ul>\r\n<li>用户反馈列表</li>\r\n</ul>\r\n</div>\r\n<table cellspacing="0" cellpadding="0">\r\n <tfoot><tr><th colspan="7"><script type="text/javascript">pager(''?site("basic/feedback/home/{0}")?'',?index?,?total?,?size?);</script></th></tr></tfoot>\r\n<tbody>\r\n<tr><th width="3%"></th><th width="57%">描述</th><th width="15%">类型</th><th width="15%">反馈时间</th><th width="10%">操作</th>\r\n</tr>\r\n<:foreach collection="?feedbackList?" var="feedback" forindex="?start?">\r\n<tr>\r\n<td>?forindex?</td>\r\n<td style="text-align:left">?feedback.Content?  <a href="?attach(''?feedback.Feedback_id?0'')?" target="_blank">附1</a>  <a href="?attach(''?feedback.Feedback_id?1'')?" target="_blank">附2</a>  <a href="?attach(''?feedback.Feedback_id?2'')?" target="_blank">附3</a></td>\r\n<td>?jtype[feedback.Jtype]?</td>\r\n<td>?idate(feedback.Feedback_id)?</td>\r\n<td><a href="#" onclick="del(''?site(''basic/feedback/remove/?feedback.Feedback_id?'')?'')">删除</a></td></tr>\r\n</:foreach>\r\n</tbody>\r\n</table>\r\n?template("base_foot")?', '1201062002169631163', 1331200640, 1334048380, '1203161054563251176', 5, ''),
('basic_invite_add', '<tr><th>数量</th><td><input type="text" name="number" id="number-digit"></td></tr>', '1201062002169631163', 1330684000, 1330685440, '1201062002169631163', 5, ''),
('basic_invite_home', '?header("team_head")? \r\n<div id="postion"><a href="?site(''jabinfo012738'')?">桌面</a>邀请码列表</div>\r\n<div class="actions">\r\n<ul>\r\n<li><a href="?site(''basic/invite/home'')?">全部</a></li>\r\n<li><a href="?site(''basic/invite/home/0'')?">未使用</a></li>\r\n<li><a href="?site(''basic/invite/home/1'')?">已使用</a></li>\r\n<li><a href="?site(''basic/invite/home/2'')?">未发放</a></li>\r\n<li><a href="#" onclick="mywin(''?site(''basic/invite/add'')?'',''?site(''basic/invite/add'')?'',''生成'')">生成邀请码</a></li>\r\n<li><a href="#" onclick="mywin(''?site(''basic/invite/remove'')?'',''?site(''basic/invite/remove'')?'','' 回收'')">回收邀请码</a></li>\r\n</ul>\r\n</div>\r\n<table  width="100%" border="0" cellspacing="0" cellpadding="0">\r\n  <tfoot><tr><th colspan="10">\r\n     <script type="text/javascript">pager(''?site("basic/invite/home/?type?/{0}")?'',?index?,?total?,?size?);  \r\n     </script>\r\n  </th></tr></tfoot>\r\n  <tbody>\r\n  <tr>\r\n<th width="3%"></th>\r\n    <th width="17%" >邀请码</th>\r\n    <th width="20%">拥有者</th>\r\n    <th width="20%">受邀者</th>\r\n    <th width="40%">添加时间</th>\r\n  </tr>\r\n  <:foreach collection="?inviteList?" var="invite" forindex="?start?">  \r\n  <tr>\r\n<td>?forindex?</td><td>?invite.Invite_id?</td><td>?TeamTitle(invite.Team_id)?</td>\r\n<td>?date(invite.Addtime)?</td></tr> \r\n  </:foreach>\r\n</table>\r\n?template("base_foot")?  ', '1201062002169631163', 1330683520, 1335082110, '1203161054563251176', 5, ''),
('basic_invite_remove', '<tr><th>开始时间</th><td><input name="starttime" type="text" id="start-date"></td></tr>\r\n<tr><th>结束时间</th><td><input name="endtime" type="text" id="end-date"></td></tr>\r\n<input type="hidden" id="act-jump">', '1201062002169631163', 1330695550, 1330695550, '1201062002169631163', 5, ''),
